import SwiftUI

// MARK: - Full-Screen Loading View

/// Full-screen loading indicator with optional message
struct LoadingView: View {
    let message: String
    var size: LoadingSize = .medium

    enum LoadingSize {
        case small, medium, large

        var scale: CGFloat {
            switch self {
            case .small: return 1.0
            case .medium: return 1.5
            case .large: return 2.0
            }
        }
    }

    @Environment(\.designTheme) private var theme
    @Environment(\.userType) private var userType

    init(_ message: String = "Loading...", size: LoadingSize = .medium) {
        self.message = message
        self.size = size
    }

    var body: some View {
        VStack(spacing: .spacing5) {
            ProgressView()
                .scaleEffect(size.scale)
                .tint(userType.accentColor)
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.body))
                .foregroundStyle(theme.color(.mutedforeground))
                .multilineTextAlignment(.center)
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .accessibilityElement(children: .combine)
        .accessibilityLabel("\(message)")
    }
}

// MARK: - Inline Loading View

/// Inline loading indicator for smaller contexts
struct InlineLoadingView: View {
    let message: String

    @Environment(\.designTheme) private var theme
    @Environment(\.userType) private var userType

    init(_ message: String = "Loading...") {
        self.message = message
    }

    var body: some View {
        HStack(spacing: .spacing3) {
            ProgressView()
                .tint(userType.accentColor)
                .accessibilityHidden(true)

            Text(message)
                .font(theme.font(.callout))
                .foregroundStyle(theme.color(.mutedforeground))
        }
        .padding(.spacing3)
        .accessibilityElement(children: .combine)
        .accessibilityLabel("\(message)")
    }
}

// MARK: - Skeleton View

/// Skeleton placeholder for content while loading
struct SkeletonView: View {
    var height: CGFloat = 20
    var cornerRadius: CGFloat = .radiusSmall

    @State private var isAnimating = false
    @Environment(\.designTheme) private var theme
    @Environment(\.accessibilityReduceMotion) private var reduceMotion

    var body: some View {
        RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
            .fill(theme.color(.muted))
            .frame(height: height)
            .overlay(
                GeometryReader { geometry in
                    RoundedRectangle(cornerRadius: cornerRadius, style: .continuous)
                        .fill(
                            LinearGradient(
                                colors: [
                                    Color.clear,
                                    theme.color(.background).opacity(0.3),
                                    Color.clear
                                ],
                                startPoint: .leading,
                                endPoint: .trailing
                            )
                        )
                        .offset(x: isAnimating ? geometry.size.width : -geometry.size.width)
                        .animation(
                            reduceMotion ? nil : Animation.linear(duration: 1.5)
                                .repeatForever(autoreverses: false),
                            value: isAnimating
                        )
                }
                .clipped()
            )
            .onAppear {
                isAnimating = true
            }
            .accessibilityLabel("Loading content")
    }
}

// MARK: - Skeleton Card

/// Pre-built skeleton card for common use cases
struct SkeletonCard: View {
    @Environment(\.designTheme) private var theme

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing3) {
            // Header
            HStack(spacing: .spacing3) {
                Circle()
                    .fill(theme.color(.muted))
                    .frame(width: 40, height: 40)

                VStack(alignment: .leading, spacing: .spacing2) {
                    SkeletonView(height: 16, cornerRadius: .radiusSmall)
                        .frame(width: 120)

                    SkeletonView(height: 12, cornerRadius: .radiusSmall)
                        .frame(width: 80)
                }
            }

            // Content
            VStack(spacing: .spacing2) {
                SkeletonView(height: 14)
                SkeletonView(height: 14)
                SkeletonView(height: 14, cornerRadius: .radiusSmall)
                    .frame(width: 200)
            }

            // Image placeholder
            SkeletonView(height: 200, cornerRadius: .radiusMedium)
        }
        .padding(.spacing4)
        .glassCard()
        .accessibilityLabel("Loading post")
    }
}

// MARK: - Skeleton List

/// Skeleton view for list items
struct SkeletonList: View {
    var itemCount: Int = 3

    var body: some View {
        VStack(spacing: .spacing4) {
            ForEach(0..<itemCount, id: \.self) { _ in
                SkeletonCard()
            }
        }
    }
}

