//
//  UserType.swift
//  AthleadAI
//
//  User type enum with theming support
//

import SwiftUI

/// User types in the AthleadAI platform with distinct visual theming
enum UserType: String, CaseIterable, Codable {
    case athlete = "Athlete"
    case parent = "Parent/Guardian"
    case collegeCoach = "College Coach"
    case clubCoach = "Club / High School Coach"

    // MARK: - Theme Properties

    /// Accent color for each user type
    var accentColor: Color {
        switch self {
        case .athlete:
            return Color(hex: "#6366F1") // Indigo/Blue
        case .clubCoach:
            return Color(hex: "#F97316") // Orange
        case .collegeCoach:
            return Color(hex: "#EF4444") // Red
        case .parent:
            return Color(hex: "#10B981") // Green
        }
    }

    /// Gradient colors for backgrounds and highlights
    var gradientColors: [Color] {
        switch self {
        case .athlete:
            return [Color(hex: "#6366F1"), Color(hex: "#8B5CF6")] // Blue to Purple
        case .clubCoach:
            return [Color(hex: "#F97316"), Color(hex: "#FB923C")] // Orange gradient
        case .collegeCoach:
            return [Color(hex: "#EF4444"), Color(hex: "#F87171")] // Red to Pink
        case .parent:
            return [Color(hex: "#10B981"), Color(hex: "#14B8A6")] // Green to Teal
        }
    }

    /// Linear gradient for this user type
    var gradient: LinearGradient {
        LinearGradient(
            colors: gradientColors,
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )
    }

    /// Secondary accent color for subtle highlights
    var secondaryAccent: Color {
        switch self {
        case .athlete:
            return Color(hex: "#818CF8") // Lighter indigo
        case .clubCoach:
            return Color(hex: "#FDBA74") // Lighter orange
        case .collegeCoach:
            return Color(hex: "#FCA5A5") // Lighter red
        case .parent:
            return Color(hex: "#6EE7B7") // Lighter green
        }
    }

    /// Icon associated with user type
    var icon: String {
        switch self {
        case .athlete:
            return "figure.run"
        case .clubCoach:
            return "whistle"
        case .collegeCoach:
            return "building.columns"
        case .parent:
            return "person.2"
        }
    }

    /// Short display name
    var shortName: String {
        switch self {
        case .athlete:
            return "Athlete"
        case .clubCoach:
            return "Club Coach"
        case .collegeCoach:
            return "College Coach"
        case .parent:
            return "Parent"
        }
    }
}

// MARK: - Environment Key

struct UserTypeKey: EnvironmentKey {
    static let defaultValue: UserType = .athlete
}

extension EnvironmentValues {
    var userType: UserType {
        get { self[UserTypeKey.self] }
        set { self[UserTypeKey.self] = newValue }
    }
}

// MARK: - View Extension

extension View {
    /// Apply user type theming to the view
    func userTypeThemed(_ userType: UserType) -> some View {
        self.environment(\.userType, userType)
    }
}

