    var body: some View {
        // Determine which tab view to show based on user type
        switch userType {
        case .athlete:
            AthleteTabView()
        case .parent:
            ParentTabView()
        case .collegeCoach:
            CollegeCoachTabView()
        case .clubCoach:
            ClubCoachTabView()
        case .none:
            // Default to athlete view if user type is not set or unknown
            // This provides a graceful fallback for edge cases
            AthleteTabView()
        }
    }

    /// Computed property to safely extract user type from Clerk public metadata
    /// Returns UserType enum value or nil if not found/invalid
    private var userType: UserType? {
        // Access the user's public metadata
        guard let metadata = clerk.user?.publicMetadata else {
            return nil
        }

        // Extract userType string from metadata
        guard let userTypeString = metadata["userType"] as? String else {
            return nil
        }

        // Match the string to our UserType enum
        // We use rawValue matching to safely convert the string
        switch userTypeString {
        case UserType.athlete.rawValue:
            return .athlete
        case UserType.parent.rawValue:
            return .parent
        case UserType.collegeCoach.rawValue:
            return .collegeCoach
        case UserType.clubCoach.rawValue:
            return .clubCoach
        default:
            return nil
        }
    }
}

