//
//  FeedViewModel.swift
//  AthleadAI
//
//  ViewModel for managing feed state and business logic
//  Supports both athlete (squad-based) and coach (filter-based) feeds
//

import SwiftUI
import Observation

@Observable
class FeedViewModel {
    // MARK: - Published State (read-only from outside)
    private(set) var posts: [FeedPostData] = []
    private(set) var isLoading = false
    private(set) var error: Error?

    // MARK: - User-controlled State (can be modified by view)
    var selectedSquad: String? = nil
    var selectedFilter: RecruitFilter? = nil
    var showCreatePost = false

    // MARK: - Feed Type
    enum FeedType {
        case athlete
        case coach
    }

    let feedType: FeedType

    // MARK: - Recruit Filter (for coach feeds)
    enum RecruitFilter {
        case watchlist
        case gradYear2025
        case gradYear2026
        case gradYear2027
        case positionOutside
        case positionSetter
        case locationTexas
    }

    // MARK: - Computed Properties
    var filteredPosts: [FeedPostData] {
        if feedType == .athlete {
            // Filter by squad for athlete feeds
            if let squad = selectedSquad {
                return posts.filter { $0.squad == squad }
            }
            return posts
        } else {
            // Filter by recruit filter for coach feeds
            if let filter = selectedFilter {
                return posts.filter { post in
