//
//  StatusBadge.swift
//  AthleadAI
//
//  Semantic status badge component with design system integration
//

import SwiftUI

/// Semantic status badge with predefined color variants
public struct StatusBadge: View {
    @Environment(\.designTheme) private var theme

    /// Badge variant determining color scheme
    public enum Variant {
        case success   // Green - for positive states like "Strong", "Cleared"
        case warning   // Orange - for cautionary states like "7 Awards"
        case info      // Blue - for informational states
        case primary   // Primary brand color
        case destructive // Red - for errors or negative states
    }

    /// Badge size options
    public enum Size {
        case small
        case medium
        case large

        var font: Font {
            switch self {
            case .small: return .caption2
            case .medium: return .caption
            case .large: return .subheadline
            }
        }

        var horizontalPadding: CGFloat {
            switch self {
            case .small: return .spacing2
            case .medium: return .spacing3
            case .large: return .spacing4
            }
        }

        var verticalPadding: CGFloat {
            switch self {
            case .small: return .spacing1
            case .medium: return .spacing1
            case .large: return .spacing2
            }
        }
    }

    let text: String
    let variant: Variant
    let size: Size

    public init(_ text: String, variant: Variant = .primary, size: Size = .medium) {
        self.text = text
        self.variant = variant
        self.size = size
    }

    public var body: some View {
        Text(text)
            .font(size.font)
            .fontWeight(.semibold)
            .foregroundStyle(foregroundColor)
            .padding(.horizontal, size.horizontalPadding)
            .padding(.vertical, size.verticalPadding)
            .background(backgroundColor)
            .clipShape(Capsule())
    }

    // MARK: - Color Helpers

    private var backgroundColor: Color {
        switch variant {
        case .success:
            return theme.color(.success)
        case .warning:
            return theme.color(.warning)
        case .info:
            return theme.color(.info)
        case .primary:
            return theme.color(.primary)
        case .destructive:
            return theme.color(.destructive)
        }
    }

    private var foregroundColor: Color {
        switch variant {
        case .success:
            return theme.color(.successforeground)
        case .warning:
            return theme.color(.warningforeground)
        case .info:
            return theme.color(.infoforeground)
        case .primary:
            return theme.color(.primaryforeground)
        case .destructive:
            return theme.color(.destructiveforeground)
        }
    }
}

// MARK: - Preview

#Preview("All Variants") {
    VStack(spacing: .spacing4) {
        // Success variants
        HStack {
            StatusBadge("Strong", variant: .success, size: .small)
            StatusBadge("Cleared", variant: .success, size: .medium)
            StatusBadge("Approved", variant: .success, size: .large)
        }

        // Warning variants
        HStack {
            StatusBadge("7 Awards", variant: .warning, size: .small)
            StatusBadge("Pending", variant: .warning, size: .medium)
            StatusBadge("Review Needed", variant: .warning, size: .large)
        }

        // Info variants
        HStack {
            StatusBadge("New", variant: .info, size: .small)
            StatusBadge("Info", variant: .info, size: .medium)
            StatusBadge("Details", variant: .info, size: .large)
        }

        // Primary variants
        HStack {
            StatusBadge("200+ Hours", variant: .primary, size: .small)
            StatusBadge("Active", variant: .primary, size: .medium)
            StatusBadge("Featured", variant: .primary, size: .large)
        }

        // Destructive variants
        HStack {
            StatusBadge("Failed", variant: .destructive, size: .small)
            StatusBadge("Error", variant: .destructive, size: .medium)
            StatusBadge("Rejected", variant: .destructive, size: .large)
        }
    }
    .padding()
    .withDesignTheme()
}

#Preview("Dark Mode") {
    VStack(spacing: .spacing4) {
