import SwiftUI

struct AthleteFeedView: View {
    @Environment(\.designTheme) private var theme
    @State private var viewModel = FeedViewModel(feedType: .athlete)

    var body: some View {
        NavigationView {
            ScrollView {
                VStack(spacing: .spacing5) {
                    // Squads Section
                    VStack(alignment: .leading, spacing: .spacing3) {
                        HStack {
                            Text("Squads")
                                .font(.title3)
                                .fontWeight(.bold)
                                .foregroundStyle(theme.color(.foreground))

                            Spacer()

                            Button(action: {}) {
                                    Image(systemName: "plus.circle.fill")
                                        .foregroundColor(.blue)
                                        .font(.title3)
                                }
                                .accessibilityLabel("Add new squad")
                                .accessibilityHint("Create or join a new squad")
                            }
                            .padding(.horizontal)

                            ScrollView(.horizontal, showsIndicators: false) {
                                HStack(spacing: .spacing4) {
                                    // All Posts (default)
                                    CircleFilter(
                                        name: "All",
                                        icon: "house.fill",
                                        isSelected: viewModel.selectedSquad == nil
                                    ) {
                                        viewModel.selectSquad(nil)
                                    }

                                    // Squad circles with custom images
                                    CircleFilter(
                                        name: "Carroll",
                                        imageName: "carroll",
                                        isSelected: viewModel.selectedSquad == "Carroll Volleyball"
                                    ) {
                                        viewModel.selectSquad("Carroll Volleyball")
                                    }

                                    CircleFilter(
                                        name: "2026",
                                        imageName: "26",
                                        isSelected: viewModel.selectedSquad == "Class of 2026"
                                    ) {
                                        viewModel.selectSquad("Class of 2026")
                                    }

                                    CircleFilter(
                                        name: "Texas",
                                        imageName: "tx",
                                        isSelected: viewModel.selectedSquad == "Texas Volleyball"
                                    ) {
                                        viewModel.selectSquad("Texas Volleyball")
                                    }
                                }
                                .padding(.horizontal)
                            }
                        }
                        .padding(.vertical, .spacing3)

                        // Feed Posts
                        VStack(spacing: .spacing4) {
                            ForEach(viewModel.filteredPosts, id: \.id) { post in
                                FeedPost(
                                    authorName: post.authorName,
                                    authorHandle: post.authorHandle,
                                    timeAgo: post.timeAgo,
                                    squad: post.squad,
                                    content: post.content,
                                    likes: post.likes,
                                    comments: post.comments,
                                    mediaType: post.mediaType
                                )
                            }
                        }
                        .padding(.horizontal)
                        .padding(.bottom, .spacing5)
                    .padding(.top, .spacing5)
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Feed")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { viewModel.toggleCreatePost() }) {
                        Image(systemName: "square.and.pencil")
                            .foregroundColor(.white)
                    }
                    .accessibilityLabel("Create new post")
                    .accessibilityHint("Opens a form to create and share a new post")
                }
            }
            .sheet(isPresented: $viewModel.showCreatePost) {
                CreatePostView()
            }
            .refreshable {
                await viewModel.refresh()
            }
            .task {
                await viewModel.loadFeed()
            }
        }
    }
}

