//
//  SearchViewModel.swift
//  AthleadAI
//
//  ViewModel for managing search state and business logic
//  Supports both athlete (schools/coaches) and coach (athlete) search
//

import SwiftUI
import Observation

@Observable
class SearchViewModel {
    // MARK: - Published State (read-only from outside)
    private(set) var isLoading = false
    private(set) var error: Error?

    // Search results (read-only from outside)
    private(set) var schools: [School] = []
    private(set) var coaches: [Coach] = []
    private(set) var athletes: [AthleteSearchResult] = []

    // MARK: - User-controlled State (can be modified by view)
    var searchText = ""
    var selectedTab: SearchTab = .schools
    var showFilters = false
    var showDetail = false
    var detailType: DetailType = .school(SchoolData(initial: "", name: "", location: "", division: "", conference: "", ranking: ""))

    // Filters (user-controlled)
    var selectedPosition: String? = nil
    var selectedGradYear: String? = nil
    var selectedLocation: String? = nil

    // MARK: - Search Type
    enum SearchType {
        case athlete  // Athletes searching for schools/coaches
        case coach    // Coaches searching for athletes
    }

    let searchType: SearchType

    // MARK: - Search Tab
    enum SearchTab: String, CaseIterable {
        case schools = "Schools"
        case coaches = "Coaches"
        case athletes = "Athletes"
    }

    // MARK: - Detail Type
    enum DetailType: Hashable {
        case school(SchoolData)
        case coach(CoachData)
    }

    struct SchoolData: Hashable {
        let initial: String
        let name: String
        let location: String
        let division: String
        let conference: String
        let ranking: String
    }

    struct CoachData: Hashable {
        let name: String
        let title: String
        let school: String
        let schoolInitial: String
        let yearsExperience: String
    }

    // MARK: - Computed Properties
    var hasError: Bool {
        error != nil
    }

    var hasActiveFilters: Bool {
        selectedPosition != nil || selectedGradYear != nil || selectedLocation != nil
    }

    var filteredSchools: [School] {
        if searchText.isEmpty {
            return schools
        }
        return schools.filter { school in
            school.name.localizedCaseInsensitiveContains(searchText) ||
            school.location.localizedCaseInsensitiveContains(searchText) ||
            school.conference.localizedCaseInsensitiveContains(searchText)
        }
    }

    var filteredCoaches: [Coach] {
        if searchText.isEmpty {
            return coaches
        }
        return coaches.filter { coach in
            coach.name.localizedCaseInsensitiveContains(searchText) ||
            coach.schoolName.localizedCaseInsensitiveContains(searchText) ||
            coach.title.localizedCaseInsensitiveContains(searchText)
        }
    }

    var filteredAthletes: [AthleteSearchResult] {
        var results = athletes

        // Apply search text filter
        if !searchText.isEmpty {
            results = results.filter { athlete in
                athlete.name.localizedCaseInsensitiveContains(searchText) ||
                athlete.position.localizedCaseInsensitiveContains(searchText) ||
                athlete.location.localizedCaseInsensitiveContains(searchText)
            }
        }

        // Apply position filter
        if let position = selectedPosition {
            results = results.filter { $0.position == position }
        }

        // Apply grad year filter
        if let year = selectedGradYear {
            results = results.filter { $0.gradYear == year }
        }

        // Apply location filter
        if let location = selectedLocation, location != "All States" {
            results = results.filter { $0.location.contains(location) }
        }

        return results
    }

    // MARK: - Initialization
    init(searchType: SearchType) {
        self.searchType = searchType
        self.selectedTab = searchType == .athlete ? .schools : .athletes
    }

    // MARK: - Actions
    @MainActor
    func loadData() async {
        setLoading(true)
        clearError()

        do {
            // Simulate network delay
            try await Task.sleep(nanoseconds: 500_000_000)

            if searchType == .athlete {
                // Load schools and coaches for athlete search
                schools = mockSchools()
                coaches = mockCoaches()
            } else {
                // Load athletes for coach search
                athletes = mockAthletes()
            }

            setLoading(false)
        } catch {
            setError(error)
            setLoading(false)
        }
    }

    @MainActor
    func performSearch() async {
        guard !searchText.isEmpty else { return }

        setLoading(true)
        clearError()

        do {
            // Simulate network delay
            try await Task.sleep(nanoseconds: 500_000_000)

            // Search is performed via filtered computed properties
            setLoading(false)
        } catch {
            setError(error)
            setLoading(false)
        }
    }

    @MainActor
    func refresh() async {
        clearError()

        do {
            // Simulate network delay
            try await Task.sleep(nanoseconds: 500_000_000)

            if searchType == .athlete {
                schools = mockSchools()
                coaches = mockCoaches()
            } else {
                athletes = mockAthletes()
            }

            // Trigger haptic feedback on successful refresh
            HapticManager.shared.success()
        } catch {
            setError(error)
            HapticManager.shared.error()
        }
    }

    func clearSearch() {
        searchText = ""
    }

    func clearFilters() {
        selectedPosition = nil
        selectedGradYear = nil
        selectedLocation = nil
    }

    func toggleFilters() {
        showFilters.toggle()
    }

    func selectTab(_ tab: SearchTab) {
        selectedTab = tab
    }

    func selectPosition(_ position: String?) {
        selectedPosition = position
    }

    func selectGradYear(_ year: String?) {
        selectedGradYear = year
    }

    func selectLocation(_ location: String?) {
        selectedLocation = location
    }

    func showSchoolDetail(initial: String, name: String, location: String, division: String, conference: String, ranking: String) {
        detailType = .school(SchoolData(
            initial: initial,
            name: name,
            location: location,
            division: division,
            conference: conference,
            ranking: ranking
        ))
        showDetail = true
    }

    func showCoachDetail(name: String, title: String, school: String, schoolInitial: String, yearsExperience: String) {
        detailType = .coach(CoachData(
            name: name,
            title: title,
            school: school,
            schoolInitial: schoolInitial,
            yearsExperience: yearsExperience
        ))
        showDetail = true
    }

    func toggleWatchlist(for athlete: AthleteSearchResult) {
        if let index = athletes.firstIndex(where: { $0.id == athlete.id }) {
            athletes[index].isWatchlisted.toggle()
        }
    }

    // MARK: - Private Helpers
    private func setLoading(_ loading: Bool) {
        isLoading = loading
    }

    private func setError(_ error: Error?) {
        self.error = error
    }

    private func clearError() {
        error = nil
    }

    // MARK: - Mock Data
    private func mockSchools() -> [School] {
        return [
            School(
                id: "1",
                name: "Stanford University",
                location: "Stanford, CA",
                division: "Division I",
                conference: "Pac-12",
                ranking: "#6",
                initial: "SU",
                athleticWebsite: "gostanford.com",
                facility: "Maples Pavilion",
                championships: "3 National Titles",
                recentRecord: "28-6 (2023)"
            ),
            School(
                id: "2",
                name: "University of North Carolina",
                location: "Chapel Hill, NC",
                division: "Division I",
                conference: "ACC",
                ranking: "#28",
                initial: "UNC",
                athleticWebsite: "goheels.com",
                facility: "Carmichael Arena",
                championships: "2 National Titles",
                recentRecord: "24-8 (2023)"
            ),
            School(
                id: "3",
                name: "University of Texas",
                location: "Austin, TX",
                division: "Division I",
                conference: "Big 12",
                ranking: "#38",
                initial: "UT",
                athleticWebsite: "texassports.com",
                facility: "Gregory Gym",
                championships: "1 National Title",
                recentRecord: "22-10 (2023)"
            )
        ]
    }

    private func mockCoaches() -> [Coach] {
        return [
            Coach(
                id: "1",
                name: "John Smith",
                title: "Head Coach",
                schoolId: "1",
                schoolName: "Stanford University",
                schoolInitial: "SU",
                yearsExperience: "15 years",
                email: "jsmith@stanford.edu",
                phone: "(650) 555-0100",
                specialization: "Outside Hitter Development",
                achievements: ["3x Conference Coach of the Year", "National Championship (2021)"]
            ),
            Coach(
                id: "2",
                name: "Sarah Johnson",
                title: "Assistant Coach",
                schoolId: "3",
                schoolName: "University of Texas",
                schoolInitial: "UT",
                yearsExperience: "8 years",
                email: "sjohnson@utexas.edu",
                phone: "(512) 555-0200",
                specialization: "Defensive Specialist",
                achievements: ["Former Olympic Athlete"]
            ),
            Coach(
                id: "3",
                name: "Mike Davis",
                title: "Head Coach",
                schoolId: "2",
                schoolName: "University of North Carolina",
                schoolInitial: "UNC",
                yearsExperience: "12 years",
                email: "mdavis@unc.edu",
                phone: "(919) 555-0300",
                specialization: "Middle Blocker Development",
                achievements: ["2x National Championship", "15+ Players to Professional League"]
            )
        ]
    }

    private func mockAthletes() -> [AthleteSearchResult] {
        return [
            AthleteSearchResult(
                id: "1",
                name: "Kinsley Young",
                position: "Outside Hitter",
                gradYear: "2026",
                location: "Southlake, TX",
                height: "5'11\"",
                gpa: "3.8",
                isWatchlisted: true
            ),
            AthleteSearchResult(
                id: "2",
                name: "Sarah Johnson",
                position: "Setter",
                gradYear: "2026",
                location: "Austin, TX",
                height: "5'9\"",
                gpa: "3.9",
                isWatchlisted: false
            ),
            AthleteSearchResult(
                id: "3",
                name: "Emma Davis",
                position: "Middle Blocker",
                gradYear: "2027",
                location: "Dallas, TX",
                height: "6'2\"",
                gpa: "3.7",
                isWatchlisted: false
            ),
            AthleteSearchResult(
                id: "4",
                name: "Olivia Martinez",
                position: "Libero",
                gradYear: "2025",
                location: "Houston, TX",
                height: "5'6\"",
                gpa: "4.0",
                isWatchlisted: true
            )
        ]
    }
}

// MARK: - Athlete Search Result Model
struct AthleteSearchResult: Identifiable {
    let id: String
    let name: String
    let position: String
    let gradYear: String
    let location: String
    let height: String
    let gpa: String
    var isWatchlisted: Bool
}

