import SwiftUI
import Observation

@Observable
class SearchViewModel {
    // MARK: - Published State (read-only from outside)
    private(set) var isLoading = false
    private(set) var error: Error?

    // Search results (read-only from outside)
    private(set) var schools: [School] = []
    private(set) var coaches: [Coach] = []
    private(set) var athletes: [AthleteSearchResult] = []

    // MARK: - User-controlled State (can be modified by view)
    var searchText = ""
    var selectedTab: SearchTab = .schools
    var showFilters = false
    var showDetail = false
