//
//  AthleteSearchView.swift
//  AthleadAI
//
//  Search view for athletes - can search for schools, coaches, and other athletes
//

import SwiftUI

struct AthleteSearchView: View {
    @Environment(\.designTheme) private var theme
    @State private var viewModel = SearchViewModel(searchType: .athlete)

    var body: some View {
        NavigationView {
            VStack(spacing: 0) {
                // Search Bar
                HStack {
                        Image(systemName: "magnifyingglass")
                            .foregroundStyle(theme.color(.mutedforeground))

                    TextField("Search schools...", text: $viewModel.searchText)
                        .foregroundStyle(theme.color(.foreground))
                }
                .liquidGlassCard()
                .padding(.horizontal)
                .padding(.vertical, .spacing3)

                // Tab Selector
                HStack(spacing: 0) {
                    ForEach(SearchViewModel.SearchTab.allCases, id: \.self) { tab in
                        Button(action: {
                            viewModel.selectTab(tab)
                        }) {
                            VStack(spacing: .spacing2) {
                                Text(tab.rawValue)
                                    .font(theme.font(.subheadline))
                                    .fontWeight(.semibold)
                                    .foregroundStyle(viewModel.selectedTab == tab ? theme.color(.foreground) : theme.color(.mutedforeground))

                                Rectangle()
                                    .fill(viewModel.selectedTab == tab ? theme.color(.primary) : Color.clear)
                                    .frame(height: 2)
                            }
                            .frame(maxWidth: .infinity)
                        }
                    }
                }
                .padding(.horizontal)
                .padding(.bottom, .spacing3)

                // Content
                if viewModel.selectedTab == .schools {
                    SchoolsListView(
                        schools: viewModel.filteredSchools,
                        isLoading: viewModel.isLoading,
                        showDetail: $viewModel.showDetail,
                        detailType: $viewModel.detailType
                    )
                } else if viewModel.selectedTab == .coaches {
                    CoachesListView(
                        coaches: viewModel.filteredCoaches,
                        isLoading: viewModel.isLoading,
                        showDetail: $viewModel.showDetail,
                        detailType: $viewModel.detailType
                    )
                } else {
                    AthletesListView()
                }
            }
            .withDesignTheme()
            .brandBackground(.feed)
            .navigationTitle("Schools & Coaches")
            .navigationBarTitleDisplayMode(.inline)
            .refreshable {
                await viewModel.refresh()
            }
            .task {
                await viewModel.loadData()
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    HStack(spacing: .spacing4) {
                        Button(action: {}) {
                            Image(systemName: "chart.bar.fill")
                                .foregroundStyle(theme.color(.primary))
                        }
                        .accessibilityLabel("View statistics")
                        .accessibilityHint("Show recruiting statistics and insights")

                        Button(action: {}) {
                            Image(systemName: "bookmark.fill")
                                .foregroundStyle(theme.color(.primary))
                        }
                        .accessibilityLabel("View bookmarks")
                        .accessibilityHint("Show saved schools and coaches")
                    }
                }
            }
            .sheet(isPresented: $viewModel.showDetail) {
                NavigationView {
                    DetailContainerView(detailType: $viewModel.detailType, showDetail: $viewModel.showDetail)
                }
            }
        }
    }
}

// Detail Container that switches between School and Coach
struct DetailContainerView: View {
    @Environment(\.designTheme) private var theme
    @Binding var detailType: SearchViewModel.DetailType
    @Binding var showDetail: Bool
    @State private var navigationStack: [SearchViewModel.DetailType] = []

    var body: some View {
        NavigationStack(path: $navigationStack) {
            Group {
                switch detailType {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                }
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarLeading) {
                    Button("Done") {
                        showDetail = false
                    }
                    .foregroundStyle(theme.color(.primary))
                }
            }
            .navigationDestination(for: SearchViewModel.DetailType.self) { destination in
                switch destination {
                case .school(let school):
                    SchoolDetailView(
                        initial: school.initial,
                        name: school.name,
                        location: school.location,
                        division: school.division,
                        conference: school.conference,
                        ranking: school.ranking,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundStyle(theme.color(.primary))
                        }
                    }
                case .coach(let coach):
                    CoachDetailView(
                        name: coach.name,
                        title: coach.title,
                        school: coach.school,
                        schoolInitial: coach.schoolInitial,
                        yearsExperience: coach.yearsExperience,
                        navigationStack: $navigationStack
                    )
                    .toolbar {
                        ToolbarItem(placement: .navigationBarLeading) {
                            Button("Done") {
                                showDetail = false
                            }
                            .foregroundStyle(theme.color(.primary))
                        }
                    }
                }
            }
        }
    }
}

// Schools List View
struct SchoolsListView: View {
    @Environment(\.designTheme) private var theme
    let schools: [School]
    let isLoading: Bool
    @Binding var showDetail: Bool
    @Binding var detailType: SearchViewModel.DetailType

    var body: some View {
        ScrollView {
            VStack(spacing: .spacing3) {
                if isLoading {
                    ProgressView()
                        .progressViewStyle(CircularProgressViewStyle(tint: theme.color(.primary)))
                        .padding()
                } else if schools.isEmpty {
                    Text("No schools found")
                        .foregroundStyle(theme.color(.mutedforeground))
                        .padding()
                } else {
                    ForEach(schools) { school in
                        SchoolCard(
                            initial: school.initial,
                            name: school.name,
                            location: school.location,
                            division: school.division,
                            conference: school.conference,
                            ranking: school.ranking ?? "N/A",
                            showDetail: $showDetail,
                            detailType: $detailType
                        )
                    }
                }
            }
            .padding()
        }
    }
}

