// Coach Card Content (reusable component for inline coach cards)
struct CoachCardContent: View {
    @Environment(\.designTheme) private var theme
    let name: String
    let title: String
    let school: String
    let schoolInitial: String
    let yearsExperience: String

    var body: some View {
        HStack(spacing: .spacing4) {
            Image(systemName: "person.crop.circle.fill")
                .resizable()
                .frame(width: 60, height: 60)
                .foregroundStyle(theme.color(.mutedforeground))

            VStack(alignment: .leading, spacing: .spacing2) {
                Text(name)
                    .font(.headline)
                    .foregroundStyle(theme.color(.foreground))

                Text(title)
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.primary))

                HStack(spacing: 6) {
                    Text(schoolInitial)
                        .font(.caption)
                        .fontWeight(.semibold)
                        .foregroundStyle(theme.color(.primary))

                    Text(school)
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))

                    Text("•")
                        .foregroundStyle(theme.color(.mutedforeground))

                    Text(yearsExperience)
                        .font(.caption)
                        .foregroundStyle(theme.color(.mutedforeground))
                }
