                    HStack(spacing: .spacing2) {
                        Text(schoolInitial)
                            .font(.caption)
                            .fontWeight(.semibold)
                            .foregroundStyle(theme.color(.primary))

                        Text(school)
                            .font(.caption)
                            .foregroundStyle(theme.color(.mutedforeground))

                        Text("•")
                            .foregroundStyle(theme.color(.mutedforeground))

                        Text(yearsExperience)
                            .font(.caption)
                            .foregroundStyle(theme.color(.mutedforeground))
                    }
                }

                Spacer()

                Image(systemName: "chevron.right")
                    .foregroundStyle(theme.color(.mutedforeground))
                    .accessibilityHidden(true)
