// School Detail View
struct SchoolDetailView: View {
    @Environment(\.designTheme) private var theme
    let initial: String
    let name: String
    let location: String
    let division: String
    let conference: String
    let ranking: String
    @Binding var navigationStack: [SearchViewModel.DetailType]

    var body: some View {
        ZStack {
            LinearGradient(
                gradient: Gradient(colors: [
                    Color(red: 0.1, green: 0.1, blue: 0.2),
                    Color(red: 0.05, green: 0.05, blue: 0.15)
                ]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()

            ScrollView {
                VStack(spacing: .spacing5) {
                    // School Header
                    VStack(spacing: .spacing4) {
                        Text(initial)
                            .font(.largeTitle)
                            .fontWeight(.bold)
                            .dynamicTypeSize(...DynamicTypeSize.accessibility1)
                            .foregroundStyle(theme.color(.primary))
                            .frame(width: 120, height: 120)
                            .background(
                                RoundedRectangle(cornerRadius: .radiusXLarge)
                                    .fill(theme.color(.primary).opacity(0.2))
                            )

                        Text(name)
                            .font(.title)
                            .fontWeight(.bold)
                            .foregroundStyle(theme.color(.foreground))

                        Text(location)
                            .font(.subheadline)
                            .foregroundStyle(theme.color(.mutedforeground))

                        HStack(spacing: .spacing4) {
                            VStack {
                                Text(division)
                                    .font(.caption)
                                    .foregroundStyle(theme.color(.mutedforeground))
                                Text(conference)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundStyle(theme.color(.foreground))
                            }

                            Divider()
                                .frame(height: 30)

                            VStack {
                                Text("Ranking")
                                    .font(.caption)
                                    .foregroundStyle(theme.color(.mutedforeground))
                                Text(ranking)
                                    .font(.subheadline)
                                    .fontWeight(.semibold)
                                    .foregroundStyle(theme.color(.primary))
                            }
                        }
                    }
                    .liquidGlassCard()
                    .padding(.horizontal)

                    // Coaching Staff Section
                    VStack(alignment: .leading, spacing: .spacing4) {
                        Text("Coaching Staff")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundStyle(theme.color(.foreground))
                            .padding(.horizontal)

                        VStack(spacing: .spacing3) {
                            NavigationLink(value: SearchViewModel.DetailType.coach(SearchViewModel.CoachData(
                                name: "John Smith",
                                title: "Head Coach",
                                school: name,
                                schoolInitial: initial,
                                yearsExperience: "15 years"
                            ))) {
                                CoachCardContent(
                                    name: "John Smith",
                                    title: "Head Coach",
                                    school: name,
                                    schoolInitial: initial,
                                    yearsExperience: "15 years"
                                )
                            }

                            NavigationLink(value: SearchViewModel.DetailType.coach(SearchViewModel.CoachData(
                                name: "Lisa Anderson",
                                title: "Assistant Coach",
                                school: name,
                                schoolInitial: initial,
                                yearsExperience: "10 years"
                            ))) {
                                CoachCardContent(
                                    name: "Lisa Anderson",
                                    title: "Assistant Coach",
                                    school: name,
                                    schoolInitial: initial,
                                    yearsExperience: "10 years"
                                )
                            }
                        }
                        .padding(.horizontal)
                    }

                    // Program Info
                    VStack(alignment: .leading, spacing: .spacing4) {
                        Text("Program Information")
                            .font(.title3)
                            .fontWeight(.bold)
                            .foregroundStyle(theme.color(.foreground))

                        VStack(alignment: .leading, spacing: .spacing3) {
                            DetailInfoRow(label: "Athletic Website", value: "athletics.school.edu")
                            DetailInfoRow(label: "Facility", value: "Main Arena (5,000 capacity)")
                            DetailInfoRow(label: "Championships", value: "3 National Titles")
                            DetailInfoRow(label: "Recent Record", value: "28-6 (2023)")
                        }
                    }
                    .liquidGlassCard()
                    .padding(.horizontal)
                }
                .padding(.vertical, .spacing5)
            }
        }
        .withDesignTheme()
        .brandBackground(.feed)
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                Button(action: {}) {
                    Image(systemName: "bookmark")
                        .foregroundStyle(theme.color(.primary))
                }
                .accessibilityLabel("Bookmark school")
                .accessibilityHint("Save this school to your bookmarks")
            }
        }
    }
}

// Coach Detail View
struct CoachDetailView: View {
    @Environment(\.designTheme) private var theme
    let name: String
    let title: String
    let school: String
    let schoolInitial: String
    let yearsExperience: String
    @Binding var navigationStack: [SearchViewModel.DetailType]

    var body: some View {
        ScrollView {
            VStack(spacing: .spacing5) {
                coachHeaderSection
                coachInfoSection
                achievementsSection
            }
            .padding(.vertical, 20)
        }
        .withDesignTheme()
        .brandBackground(.feed)
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .navigationBarTrailing) {
                Button(action: {}) {
                    Image(systemName: "star")
                        .foregroundStyle(theme.color(.foreground))
                }
                .accessibilityLabel("Add to watchlist")
                .accessibilityHint("Add this coach to your recruiting watchlist")
            }
        }
    }

    private var coachHeaderSection: some View {
        VStack(spacing: .spacing4) {
            Image(systemName: "person.crop.circle.fill")
                .resizable()
                .frame(width: 120, height: 120)
                .foregroundStyle(theme.color(.mutedforeground))

            Text(name)
                .font(.title)
                .fontWeight(.bold)
                .foregroundStyle(theme.color(.foreground))

            Text(title)
                .font(.headline)
                .foregroundStyle(theme.color(.primary))

            schoolLinkButton
        }
        .liquidGlassCard()
        .padding(.horizontal)
    }

    private var schoolLinkButton: some View {
        NavigationLink(value: SearchViewModel.DetailType.school(SearchViewModel.SchoolData(
            initial: schoolInitial,
            name: school,
            location: "Location",
            division: "Division I",
            conference: "Conference",
            ranking: "#1"
        ))) {
            HStack(spacing: .spacing2) {
                Text(schoolInitial)
                    .font(.caption)
                    .fontWeight(.semibold)
                    .foregroundStyle(theme.color(.primary))
                    .frame(width: 40, height: 40)
                    .background(
                        RoundedRectangle(cornerRadius: .radiusSmall)
                            .fill(theme.color(.primary).opacity(0.2))
                    )

                Text(school)
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.foreground))

                Image(systemName: "chevron.right")
                    .font(.caption)
                    .foregroundStyle(theme.color(.mutedforeground))
                    .accessibilityHidden(true)
            }
            .liquidGlassCard()
        }
    }

    private var coachInfoSection: some View {
        VStack(alignment: .leading, spacing: .spacing4) {
            Text("About")
                .font(.title3)
                .fontWeight(.bold)
                .foregroundStyle(theme.color(.foreground))

            VStack(alignment: .leading, spacing: .spacing3) {
                DetailInfoRow(label: "Experience", value: yearsExperience)
                DetailInfoRow(label: "Email", value: "coach@school.edu")
                DetailInfoRow(label: "Phone", value: "(555) 123-4567")
                DetailInfoRow(label: "Specialization", value: "Middle Blocker Development")
            }
        }
        .liquidGlassCard()
        .padding(.horizontal)
    }

    private var achievementsSection: some View {
        VStack(alignment: .leading, spacing: .spacing4) {
            Text("Achievements")
                .font(.title3)
                .fontWeight(.bold)
                .foregroundStyle(theme.color(.foreground))

            VStack(alignment: .leading, spacing: .spacing3) {
                AchievementRow(text: "3x Conference Coach of the Year")
                AchievementRow(text: "National Championship (2021)")
                AchievementRow(text: "15+ Players to Professional League")
            }
        }
        .liquidGlassCard()
        .padding(.horizontal)
    }
}

