struct DetailInfoRow: View {
    @Environment(\.designTheme) private var theme
    let label: String
    let value: String

    var body: some View {
        HStack {
            Text(label)
                .font(.subheadline)
                .foregroundStyle(theme.color(.mutedforeground))
            Spacer()
            Text(value)
                .font(.subheadline)
                .foregroundStyle(theme.color(.foreground))
        }
    }
}

// Achievement Row Component
struct AchievementRow: View {
    @Environment(\.designTheme) private var theme
    let text: String

    var body: some View {
        HStack(spacing: .spacing3) {
            Image(systemName: "star.fill")
                .foregroundStyle(theme.color(.warning))
            Text(text)
                .font(theme.font(.subheadline))
                .foregroundStyle(theme.color(.foreground))
        }
    }
}

// Filter Options View
struct FilterOptionsView: View {
    @Environment(\.designTheme) private var theme
    let viewModel: SearchViewModel

    var body: some View {
        VStack(alignment: .leading, spacing: .spacing3) {
            HStack {
                Text("Filters")
                    .font(.headline)
                    .foregroundStyle(theme.color(.foreground))

                Spacer()

                if viewModel.hasActiveFilters {
                    Button("Clear All") {
                        withAnimation {
                            viewModel.clearFilters()
                        }
                    }
                    .font(.caption)
                    .foregroundStyle(theme.color(.destructive))
                }
            }

            // Division Filter (Schools)
            if viewModel.selectedTab == .schools {
                VStack(alignment: .leading, spacing: .spacing2) {
                    Text("Division")
                        .font(.subheadline)
                        .foregroundStyle(theme.color(.mutedforeground))

                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: .spacing2) {
                            FilterChip(
                                title: "Division I",
                                isSelected: viewModel.selectedDivision == "Division I",
                                action: {
                                    viewModel.selectedDivision = viewModel.selectedDivision == "Division I" ? nil : "Division I"
                                }
                            )
                            FilterChip(
                                title: "Division II",
                                isSelected: viewModel.selectedDivision == "Division II",
                                action: {
                                    viewModel.selectedDivision = viewModel.selectedDivision == "Division II" ? nil : "Division II"
                                }
                            )
                            FilterChip(
                                title: "Division III",
                                isSelected: viewModel.selectedDivision == "Division III",
                                action: {
                                    viewModel.selectedDivision = viewModel.selectedDivision == "Division III" ? nil : "Division III"
                                }
                            )
                        }
                    }
                }

                // Conference Filter
                VStack(alignment: .leading, spacing: .spacing2) {
                    Text("Conference")
                        .font(.subheadline)
                        .foregroundStyle(theme.color(.mutedforeground))

                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: .spacing2) {
                            FilterChip(
                                title: "ACC",
                                isSelected: viewModel.selectedConference == "ACC",
                                action: {
                                    viewModel.selectedConference = viewModel.selectedConference == "ACC" ? nil : "ACC"
                                }
                            )
                            FilterChip(
                                title: "Big 12",
                                isSelected: viewModel.selectedConference == "Big 12",
                                action: {
                                    viewModel.selectedConference = viewModel.selectedConference == "Big 12" ? nil : "Big 12"
                                }
                            )
                            FilterChip(
                                title: "Pac-12",
                                isSelected: viewModel.selectedConference == "Pac-12",
                                action: {
                                    viewModel.selectedConference = viewModel.selectedConference == "Pac-12" ? nil : "Pac-12"
                                }
                            )
                            FilterChip(
                                title: "Big Ten",
                                isSelected: viewModel.selectedConference == "Big Ten",
                                action: {
                                    viewModel.selectedConference = viewModel.selectedConference == "Big Ten" ? nil : "Big Ten"
                                }
                            )
                            FilterChip(
                                title: "SEC",
                                isSelected: viewModel.selectedConference == "SEC",
                                action: {
                                    viewModel.selectedConference = viewModel.selectedConference == "SEC" ? nil : "SEC"
                                }
                            )
                        }
                    }
                }
            }

            // Coach Title Filter (Coaches)
            if viewModel.selectedTab == .coaches {
                VStack(alignment: .leading, spacing: .spacing2) {
                    Text("Position")
                        .font(.subheadline)
                        .foregroundStyle(theme.color(.mutedforeground))

                    ScrollView(.horizontal, showsIndicators: false) {
                        HStack(spacing: .spacing2) {
                            FilterChip(
                                title: "Head Coach",
                                isSelected: viewModel.selectedCoachTitle == "Head Coach",
                                action: {
                                    viewModel.selectedCoachTitle = viewModel.selectedCoachTitle == "Head Coach" ? nil : "Head Coach"
                                }
                            )
                            FilterChip(
                                title: "Assistant Coach",
                                isSelected: viewModel.selectedCoachTitle == "Assistant Coach",
                                action: {
                                    viewModel.selectedCoachTitle = viewModel.selectedCoachTitle == "Assistant Coach" ? nil : "Assistant Coach"
                                }
                            )
                            FilterChip(
                                title: "Associate Coach",
                                isSelected: viewModel.selectedCoachTitle == "Associate Coach",
                                action: {
                                    viewModel.selectedCoachTitle = viewModel.selectedCoachTitle == "Associate Coach" ? nil : "Associate Coach"
                                }
                            )
                        }
                    }
                }
            }

            // State Filter (Common for both)
            VStack(alignment: .leading, spacing: .spacing2) {
                Text("State")
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.mutedforeground))

                ScrollView(.horizontal, showsIndicators: false) {
                    HStack(spacing: .spacing2) {
                        FilterChip(
                            title: "CA",
                            isSelected: viewModel.selectedState == "CA",
                            action: {
                                viewModel.selectedState = viewModel.selectedState == "CA" ? nil : "CA"
                            }
                        )
                        FilterChip(
                            title: "TX",
                            isSelected: viewModel.selectedState == "TX",
                            action: {
                                viewModel.selectedState = viewModel.selectedState == "TX" ? nil : "TX"
                            }
                        )
                        FilterChip(
                            title: "NC",
                            isSelected: viewModel.selectedState == "NC",
                            action: {
                                viewModel.selectedState = viewModel.selectedState == "NC" ? nil : "NC"
                            }
                        )
                        FilterChip(
                            title: "FL",
                            isSelected: viewModel.selectedState == "FL",
                            action: {
                                viewModel.selectedState = viewModel.selectedState == "FL" ? nil : "FL"
                            }
                        )
                        FilterChip(
                            title: "NY",
                            isSelected: viewModel.selectedState == "NY",
                            action: {
                                viewModel.selectedState = viewModel.selectedState == "NY" ? nil : "NY"
                            }
                        )
                    }
                }
            }
        }
        .liquidGlassCard()
        .padding(.horizontal)
        .padding(.bottom, .spacing3)
    }
}

// Filter Chip Component
struct FilterChip: View {
    @Environment(\.designTheme) private var theme
    let title: String
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action) {
            Text(title)
                .font(.caption)
                .fontWeight(.medium)
                .foregroundStyle(isSelected ? Color.white : theme.color(.foreground))
                .padding(.horizontal, .spacing3)
                .padding(.vertical, .spacing2)
                .background(
                    Capsule()
                        .fill(isSelected ? theme.color(.primary) : theme.color(.muted))
                )
        }
        .accessibilityLabel(title)
        .accessibilityAddTraits(isSelected ? [.isSelected] : [])
    }
}

// Athletes List View
struct AthletesListView: View {
    var body: some View {
        ScrollView {
            VStack(spacing: .spacing3) {
                NavigationLink(destination: PublicProfileView(user: User.sampleUser)) {
                    AthleteCard(user: User.sampleUser)
                }
            }
            .padding()
        }
    }
}

// Athlete Card Component
struct AthleteCard: View {
    @Environment(\.designTheme) private var theme
    let user: User

    var body: some View {
        HStack(spacing: .spacing4) {
            // Profile Image
            Image(user.profileImageName)
                .resizable()
                .aspectRatio(contentMode: .fill)
                .frame(width: 60, height: 60)
                .clipShape(Circle())
                .overlay(
                    Circle()
                        .stroke(theme.color(.primary), lineWidth: 2)
                )
                .accessibilityLabel("\(user.fullName)'s profile picture")

            VStack(alignment: .leading, spacing: .spacing2) {
                Text(user.fullName)
                    .font(.headline)
                    .foregroundStyle(theme.color(.foreground))

                Text("\(user.sport) • \(user.position)")
                    .font(.subheadline)
                    .foregroundStyle(theme.color(.primary))

                Text(user.classYear)
                    .font(.caption)
                    .foregroundStyle(theme.color(.mutedforeground))
            }

            Spacer()

            Image(systemName: "chevron.right")
                .foregroundStyle(theme.color(.mutedforeground))
                .accessibilityHidden(true)
        }
        .liquidGlassCard()
        .accessibilityElement(children: .combine)
    }
}

