/* Custom Athlead Theme Styles */

:root {
  --athlead-blue: #3b82f6;
  --athlead-purple: #8b5cf6;
  --athlead-dark: #0a0a0a;
  --athlead-dark-alt: #1a1a2e;
}

/* Custom gradient backgrounds */
.slidev-layout {
  background: linear-gradient(135deg, var(--athlead-dark) 0%, var(--athlead-dark-alt) 100%);
}

/* Enhanced headings */
h1 {
  background: linear-gradient(135deg, var(--athlead-blue) 0%, var(--athlead-purple) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-weight: 800;
}

h2 {
  color: var(--athlead-blue);
  font-weight: 700;
}

h3 {
  color: var(--athlead-purple);
  font-weight: 600;
}

/* Custom table styling */
table {
  border-collapse: collapse;
  width: 100%;
  margin: 1rem 0;
}

table th {
  background: linear-gradient(135deg, var(--athlead-blue) 0%, var(--athlead-purple) 100%);
  padding: 0.75rem;
  text-align: left;
  font-weight: 600;
}

table td {
  padding: 0.75rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

table tr:hover {
  background-color: rgba(59, 130, 246, 0.1);
}

/* Custom list styling */
ul li, ol li {
  margin-bottom: 0.5rem;
  line-height: 1.6;
}

/* Clickable animations */
.slidev-vclick-target {
  transition: all 0.3s ease;
}

.slidev-vclick-hidden {
  opacity: 0;
  transform: translateY(10px);
}

/* Custom card styling */
.custom-card {
  padding: 1.5rem;
  border-radius: 0.5rem;
  background: rgba(59, 130, 246, 0.1);
  border-left: 4px solid var(--athlead-blue);
  margin: 1rem 0;
}

/* Metric boxes */
.metric-box {
  padding: 1.5rem;
  border-radius: 0.5rem;
  background: linear-gradient(135deg, var(--athlead-blue) 0%, var(--athlead-purple) 100%);
  text-align: center;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
}

/* Quote styling */
blockquote, em {
  font-style: italic;
  border-left: 3px solid var(--athlead-purple);
  padding-left: 1rem;
  margin: 1rem 0;
  opacity: 0.9;
}

/* Code blocks */
code {
  background-color: rgba(139, 92, 246, 0.2);
  padding: 0.2rem 0.4rem;
  border-radius: 0.25rem;
  font-family: 'Fira Code', monospace;
}

/* Links */
a {
  color: var(--athlead-blue);
  text-decoration: none;
  border-bottom: 2px solid transparent;
  transition: border-color 0.3s ease;
}

a:hover {
  border-bottom-color: var(--athlead-blue);
}

/* Strong emphasis */
strong {
  color: var(--athlead-blue);
  font-weight: 700;
}

/* Slide numbers */
.slidev-page-indicator {
  background: linear-gradient(135deg, var(--athlead-blue) 0%, var(--athlead-purple) 100%);
  padding: 0.5rem 1rem;
  border-radius: 0.25rem;
  font-weight: 600;
}

