// This file is automatically synced from api-server/src/schemas/ofac.ts
// Do not edit this file directly - make changes in the api-server source
// Run 'npm run sync-schemas' to update

import { z } from 'zod'
import { secureText } from './security'

/**
 * OFAC validation schemas with comprehensive security measures
 */

// Business types for OFAC screening
const businessTypeSchema = z.enum([
  'corporation',
  'llc',
  'partnership',
  'sole_proprietorship',
  'non_profit',
  'government',
  'financial_institution',
  'money_service_business',
  'virtual_currency_business',
  'other'
], { errorMap: () => ({ message: 'Please select a valid business type' }) })

// Country validation for OFAC (more restrictive than general country codes)
const ofacCountrySchema = z.string()
  .length(2, 'Country code must be exactly 2 characters')
  .regex(/^[A-Z]{2}$/, 'Country code must be uppercase letters only')
  .refine(
    (code) => {
      // Add validation for sanctioned countries if needed
      const sanctionedCountries = ['IR', 'KP', 'SY', 'CU'] // Example sanctioned countries
      // This could be expanded based on current OFAC sanctions
      return true // For now, allow all countries but flag sanctioned ones
    },
    { message: 'Country may be subject to OFAC sanctions' }
  )

// Optional country schema that allows empty strings
const ofacCountryOptionalSchema = z.string()
  .optional()
  .refine(
    (val) => {
      if (!val || val.trim() === '') return true
      return val.length === 2 && /^[A-Z]{2}$/.test(val)
    },
    { message: 'Country code must be exactly 2 uppercase letters' }
  )

// Primary contact name validation (stricter for OFAC compliance)
// Only validates if field has content (allows empty strings for optional usage)
const primaryContactSchema = secureText(100)
  .refine(
    (val) => !val || val.length === 0 || val.length >= 2,
    'Primary contact name must be at least 2 characters'
  )
  .refine(
    (name) => {
      // Allow empty strings, otherwise ensure it contains at least one letter
      if (!name || name.trim().length === 0) return true
      return /[a-zA-Z]/.test(name) && !/^[0-9\s\-_.]+$/.test(name)
    },
    { message: 'Primary contact must be a valid person name' }
  )

// OFAC screening validation schema - supports flexible searching for businesses and individuals
export const ofacScreeningSchema = z.object({
  // Search type selector
  searchType: z.enum(['business', 'individual'], {
    errorMap: () => ({ message: 'Search type must be either business or individual' })
  }),

  // Business fields (optional when searchType is 'business')
  businessName: secureText(200)
    .optional()
    .refine(
      (name) => {
        // Allow empty/undefined, otherwise validate
        if (!name || name.trim() === '') return true
        // Business name should contain letters and not be just numbers/symbols
        return /[a-zA-Z]/.test(name) && name.trim().length >= 2
      },
      { message: 'Business name must contain letters and be meaningful' }
    ),

  businessType: businessTypeSchema.optional(),

  // Individual fields (optional when searchType is 'individual')
  individualName: secureText(200)
    .optional()
    .refine(
      (name) => {
        // Allow empty/undefined, otherwise validate
        if (!name || name.trim() === '') return true
        // Individual name should contain letters
        return /[a-zA-Z]/.test(name) && name.trim().length >= 2
      },
      { message: 'Individual name must contain letters and be meaningful' }
    ),

  dateOfBirth: z.string()
    .optional()
    .refine(
      (val) => !val || val.trim() === '' || /^\d{4}-\d{2}-\d{2}$/.test(val),
      { message: 'Date of birth must be in YYYY-MM-DD format' }
    ),

  placeOfBirth: secureText(100).optional(),

  nationality: secureText(100).optional(),

  citizenship: secureText(100).optional(),

  // Legacy field - kept for backwards compatibility
  primaryContact: primaryContactSchema.optional(),

  // Country (optional for both types)
  country: ofacCountryOptionalSchema,

  // Optional additional fields for enhanced screening (both types)
  additionalNames: z.array(secureText(100))
    .max(10, 'Maximum 10 additional names allowed')
    .optional()
    .transform((names) => names?.filter(name => name && name.trim().length > 0) || []),

  addresses: z.array(z.object({
    street: secureText(200).refine(val => val.length >= 5, 'Street address must be at least 5 characters'),
    city: secureText(50).refine(val => val.length >= 2, 'City must be at least 2 characters'),
    state: secureText(50).optional(),
    postalCode: secureText(20).optional(),
    country: ofacCountrySchema,
  }))
    .max(5, 'Maximum 5 addresses allowed')
    .optional()
    .default([]),

  identifiers: z.array(z.object({
    type: z.enum([
      'tax_id',
      'registration_number',
      'license_number',
      'passport',
      'national_id',
      'ssn',
      'other'
    ]),
    value: secureText(50).refine(val => val.length >= 3, 'Identifier must be at least 3 characters'),
    country: ofacCountrySchema.optional(),
  }))
    .max(5, 'Maximum 5 identifiers allowed')
    .optional()
    .default([]),

  // Screening options
  screeningOptions: z.object({
    includeAlternateNames: z.boolean().default(true),
    includeFuzzyMatching: z.boolean().default(false), // Disabled by default (simplified)
    includePhoneticMatching: z.boolean().default(false),
    confidenceThreshold: z.number()
      .min(0.1, 'Confidence threshold must be at least 0.1')
      .max(1.0, 'Confidence threshold must be at most 1.0')
      .default(0.8),
  }).optional().default({}),
}).refine(
  (data) => {
    // At least ONE search field must be provided
    const hasBusinessName = data.businessName && data.businessName.trim().length >= 2
    const hasIndividualName = data.individualName && data.individualName.trim().length >= 2
    const hasIdentifiers = data.identifiers && data.identifiers.length > 0
    const hasAdditionalNames = data.additionalNames && data.additionalNames.length > 0
    const hasAddresses = data.addresses && data.addresses.length > 0
    const hasDateOfBirth = data.dateOfBirth && data.dateOfBirth.length > 0
    const hasNationality = data.nationality && data.nationality.trim().length > 0

    return hasBusinessName ||
           hasIndividualName ||
           hasIdentifiers ||
           hasAdditionalNames ||
           hasAddresses ||
           hasDateOfBirth ||
           hasNationality
  },
  {
    message: 'Please provide at least one search criteria (name, identifier, date of birth, nationality, or address)',
    path: ['businessName'] // Show error on the first field
  }
).refine(
  (data) => {
    // Validate that business fields are provided when searchType is 'business'
    if (data.searchType === 'business') {
      const hasBusinessName = data.businessName && data.businessName.trim().length >= 2
      const hasIdentifiers = data.identifiers && data.identifiers.length > 0
      const hasAdditionalNames = data.additionalNames && data.additionalNames.length > 0

      return hasBusinessName || hasIdentifiers || hasAdditionalNames
    }
    return true
  },
  {
    message: 'For business searches, please provide at least business name, identifier, or additional name',
    path: ['businessName']
  }
).refine(
  (data) => {
    // Validate that individual fields are provided when searchType is 'individual'
    if (data.searchType === 'individual') {
      const hasIndividualName = data.individualName && data.individualName.trim().length >= 2
      const hasIdentifiers = data.identifiers && data.identifiers.length > 0
      const hasDateOfBirth = data.dateOfBirth && data.dateOfBirth.length > 0
      const hasNationality = data.nationality && data.nationality.trim().length > 0

      return hasIndividualName || hasIdentifiers || hasDateOfBirth || hasNationality
    }
    return true
  },
  {
    message: 'For individual searches, please provide at least name, identifier, date of birth, or nationality',
    path: ['individualName']
  }
)

// OFAC monitoring setup validation schema
export const ofacMonitoringSchema = z.object({
  businessName: secureText(200).refine(val => val.length >= 2, 'Business name is required'),

  monitoringFrequency: z.enum([
    'daily',
    'weekly',
    'monthly',
    'quarterly',
    'on_demand'
  ], { errorMap: () => ({ message: 'Please select a valid monitoring frequency' }) }),

  alertThreshold: z.enum([
    'any_match',
    'high_confidence',
    'exact_match_only'
  ], { errorMap: () => ({ message: 'Please select a valid alert threshold' }) }),

  notificationEmail: z.string()
    .email('Invalid email format')
    .max(254, 'Email must be 254 characters or less'),

  includeWatchlists: z.array(z.enum([
    'sdn',           // Specially Designated Nationals
    'consolidated',  // Consolidated Sanctions List
    'sectoral',      // Sectoral Sanctions Identifications
    'fse',          // Foreign Sanctions Evaders
    'isa',          // Iranian Sanctions Act
    'pep',          // Politically Exposed Persons
    'custom'        // Custom watch lists
  ])).min(1, 'At least one watchlist must be selected'),

  active: z.boolean().default(true),
})

// OFAC batch processing validation schema
export const ofacBatchSchema = z.object({
  batchName: secureText(100).refine(val => val.length >= 3, 'Batch name must be at least 3 characters'),

  entities: z.array(z.object({
    id: secureText(50).refine(val => val.length >= 1, 'Entity ID is required'),
    name: secureText(200).refine(val => val.length >= 2, 'Entity name must be at least 2 characters'),
    type: z.enum(['individual', 'business', 'vessel', 'aircraft']),
    country: ofacCountrySchema.optional(),
    additionalInfo: secureText(500).optional(),
  }))
    .min(1, 'At least one entity is required for batch processing')
    .max(1000, 'Maximum 1000 entities allowed per batch'),

  processingOptions: z.object({
    notifyOnCompletion: z.boolean().default(true),
    notificationEmail: z.string().email().optional(),
    priority: z.enum(['low', 'normal', 'high']).default('normal'),
    includeDetailedResults: z.boolean().default(true),
  }).optional().default({}),
})

// OFAC review decision validation schema
export const ofacReviewSchema = z.object({
  screeningId: secureText(50).refine(val => val.length >= 1, 'Screening ID is required'),

  decision: z.enum([
    'approve',
    'deny',
    'escalate',
    'request_more_info'
  ], { errorMap: () => ({ message: 'Please select a valid review decision' }) }),

  reviewNotes: secureText(2000)
    .refine(val => val.length >= 10, 'Review notes must be at least 10 characters'),

  riskRating: z.enum([
    'low',
    'medium',
    'high',
    'critical'
  ], { errorMap: () => ({ message: 'Please select a valid risk rating' }) }),

  followUpRequired: z.boolean().default(false),

  followUpDate: z.string()
    .regex(/^\d{4}-\d{2}-\d{2}$/, 'Follow-up date must be in YYYY-MM-DD format')
    .optional()
    .refine((date, ctx) => {
      if (ctx.parent.followUpRequired && !date) {
        return false
      }
      if (date) {
        const followUpDate = new Date(date)
        const today = new Date()
        today.setHours(0, 0, 0, 0)
        return followUpDate >= today
      }
      return true
    }, 'Follow-up date must be today or in the future when follow-up is required'),

  escalationReason: secureText(500).optional(),

  attachments: z.array(z.object({
    filename: secureText(255),
    size: z.number().max(10485760, 'File size must not exceed 10MB'), // 10MB limit
    type: z.enum(['pdf', 'doc', 'docx', 'txt', 'jpg', 'png']),
  }))
    .max(5, 'Maximum 5 attachments allowed')
    .optional()
    .default([]),
}).refine(
  (data) => {
    // If escalating, escalation reason is required
    if (data.decision === 'escalate' && !data.escalationReason) {
      return false
    }
    return true
  },
  {
    message: 'Escalation reason is required when escalating a case',
    path: ['escalationReason']
  }
)

// Type exports for TypeScript
export type OFACScreeningData = z.infer<typeof ofacScreeningSchema>
export type OFACMonitoringData = z.infer<typeof ofacMonitoringSchema>
export type OFACBatchData = z.infer<typeof ofacBatchSchema>
export type OFACReviewData = z.infer<typeof ofacReviewSchema>

