// Debug logging with error details
const debugSubmit = useCallback(async (data: any) => {
  console.log('=== OFAC Form Submission Debug ===')
  console.log('Search Type:', searchType)
  console.log('Form Data:', data)
  console.log('Form Valid:', form.formState.isValid)
  console.log('Form Errors:', form.formState.errors)
  console.log('Form Dirty Fields:', form.formState.dirtyFields)
  console.log('Form Touched Fields:', form.formState.touchedFields)
  console.log('Is Submitting:', isSubmitting)
  console.log('Is Pending:', isPending)

  // Log all form values for debugging
  const allValues = form.getValues()
  console.log('All Form Values:', allValues)

  // Try to manually validate to see the actual error
  try {
    const result = ofacScreeningSchema.safeParse(allValues)
    console.log('Manual Zod Validation:', result)
    if (!result.success) {
      console.log('Validation Errors:', result.error.errors)
    }
  } catch (err) {
    console.error('Validation Error:', err)
  }

  handleSubmit(data)
}, [searchType, form, isSubmitting, isPending, handleSubmit])

