'use client'

import { useState } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { Search, AlertTriangle, CheckCircle, XCircle, Clock, User, Building2 } from 'lucide-react'

/**
 * ScreeningResult interface with defensive typing
 * Note: matchedEntities comes from database JSONB field which can be null/undefined
 * Individual fields within entities may also be null/undefined
 */
interface ScreeningResult {
  id: string
  merchantName: string | null
  businessType: string | null
  hasMatch: boolean
  matchScore: number | null
  status: 'completed' | 'pending' | 'failed'
  checkedAt: string
  // JSONB field from database - can be null, undefined, or malformed
  matchedEntities?: Array<{
    primaryName: string
    entityType: string
    // Arrays may be null/undefined from JSONB
    programs?: string[] | null
    lists?: string[] | null
    matchScore: number
    aliases?: string[] | null
    remarks?: string | null
  }> | null
}

interface ScreeningResultsProps {
  initialResults: ScreeningResult[]
}

function getStatusIcon(status: string, hasMatch: boolean) {
  if (status === 'pending') return <Clock className="h-4 w-4 text-yellow-500" />
  if (status === 'failed') return <XCircle className="h-4 w-4 text-red-500" />
  if (hasMatch) return <AlertTriangle className="h-4 w-4 text-red-500" />
  return <CheckCircle className="h-4 w-4 text-green-500" />
}

function getStatusBadge(status: string, hasMatch: boolean) {
  if (status === 'pending') return <Badge variant="secondary">Pending</Badge>
  if (status === 'failed') return <Badge variant="destructive">Failed</Badge>
  if (hasMatch) return <Badge variant="destructive">Match Found</Badge>
  return <Badge variant="secondary" className="bg-green-100 text-green-800">Clear</Badge>
}

function getRiskLevel(score: number) {
  if (score >= 0.8) return { level: 'High Risk', color: 'text-red-600', bg: 'bg-red-100' }
  if (score >= 0.5) return { level: 'Medium Risk', color: 'text-yellow-600', bg: 'bg-yellow-100' }
  return { level: 'Low Risk', color: 'text-green-600', bg: 'bg-green-100' }
}

export function ScreeningResults({ initialResults = [] }: ScreeningResultsProps) {
  const [searchTerm, setSearchTerm] = useState('')

  // Defensive: Ensure initialResults is always an array
  const safeResults = Array.isArray(initialResults) ? initialResults : []

  // Use initialResults directly instead of storing in state
  // This ensures the component updates when the server component re-fetches data
  // after a successful screening submission (triggered by router.refresh())
  const filteredResults = safeResults.filter(result => {
    const merchantName = result.merchantName || ''
    const businessType = result.businessType || ''
    const searchLower = searchTerm.toLowerCase()
    return merchantName.toLowerCase().includes(searchLower) ||
           businessType.toLowerCase().includes(searchLower)
  })

  return (
    <Card>
      <CardHeader>
        <CardTitle>Recent Screening Results</CardTitle>
        <CardDescription>
          View and manage recent OFAC screening results
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className="mb-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search merchants..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-9"
            />
          </div>
        </div>

        <div className="space-y-4">
          {filteredResults.length === 0 ? (
            <div className="text-center py-12">
              <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-gray-100 mb-4">
                <Search className="h-8 w-8 text-gray-400" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                {searchTerm ? 'No results found' : 'No screening results yet'}
              </h3>
              <p className="text-gray-600">
                {searchTerm
                  ? 'Try adjusting your search terms'
                  : 'Submit a screening request above to get started'}
              </p>
            </div>
          ) : (
            filteredResults.map((result) => (
              <Card key={result.id} className="border-l-4 border-l-gray-200">
              <CardContent className="p-4">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      {getStatusIcon(result.status, result.hasMatch)}
                      <div>
                        <h3 className="font-semibold text-gray-900 flex items-center gap-2">
                          <Building2 className="h-4 w-4" />
                          {result.merchantName || 'Unknown Merchant'}
                        </h3>
                        <p className="text-sm text-gray-600">{result.businessType || 'Unknown Type'}</p>
                      </div>
                    </div>

                    <div className="flex items-center gap-4 text-sm text-gray-600 mb-3">
                      <span>Screened: {new Date(result.checkedAt).toLocaleString()}</span>
                      <span>Score: {((result.matchScore || 0) * 100).toFixed(0)}%</span>
                      {result.hasMatch && result.matchScore !== null && result.matchScore > 0 && (
                        <span className={`px-2 py-1 rounded text-xs font-medium ${getRiskLevel(result.matchScore).bg} ${getRiskLevel(result.matchScore).color}`}>
                          {getRiskLevel(result.matchScore).level}
                        </span>
                      )}
                    </div>

                    {/*
                      Defensive null checks:
                      - matchedEntities can be null/undefined from database JSONB field
                      - Array.isArray ensures it's actually an array before calling .map()
                      - Individual entity fields (programs, lists, aliases) also need null safety
                      - Filter out entities with 0% match score (likely data quality issues)
                    */}
                    {result.matchedEntities && Array.isArray(result.matchedEntities) &&
                     result.matchedEntities.filter(e => e.matchScore > 0).length > 0 && (
                      <Alert className="border-red-200 bg-red-50">
                        <AlertTriangle className="h-4 w-4" />
                        <AlertDescription>
                          <div className="space-y-2">
                            <p className="font-medium">Potential OFAC Match Found:</p>
                            {result.matchedEntities.filter(e => e.matchScore > 0).map((entity, idx) => (
                              <div key={idx} className="text-sm space-y-1">
                                <div className="flex items-center gap-2">
                                  <User className="h-3 w-3" />
                                  <span className="font-medium">{entity.primaryName}</span>
                                  <Badge variant="outline" className="text-xs">
                                    {entity.entityType}
                                  </Badge>
                                  <span className="text-red-600 font-medium">
                                    {(entity.matchScore * 100).toFixed(0)}% match
                                  </span>
                                </div>
                                <div className="ml-5 space-y-1">
                                  {/* Defensive check: programs array may be null/undefined from JSONB */}
                                  {entity.programs && Array.isArray(entity.programs) && entity.programs.length > 0 && (
                                    <div className="flex gap-1 flex-wrap">
                                      <span className="text-xs text-gray-600">Programs:</span>
                                      {entity.programs.map(program => (
                                        <Badge key={program} variant="outline" className="text-xs">
                                          {program}
                                        </Badge>
                                      ))}
                                    </div>
                                  )}
                                  {/* Defensive check: lists array may be null/undefined from JSONB */}
                                  {entity.lists && Array.isArray(entity.lists) && entity.lists.length > 0 && (
                                    <div className="flex gap-1 flex-wrap">
                                      <span className="text-xs text-gray-600">Lists:</span>
                                      {entity.lists.map(list => (
                                        <Badge key={list} variant="outline" className="text-xs">
                                          {list}
                                        </Badge>
                                      ))}
                                    </div>
                                  )}
                                  {entity.aliases && Array.isArray(entity.aliases) && entity.aliases.length > 0 && (
                                    <div>
                                      <span className="text-xs text-gray-600">Aliases: </span>
                                      <span className="text-xs">{entity.aliases.join(', ')}</span>
                                    </div>
                                  )}
                                  {entity.remarks && (
                                    <div>
                                      <span className="text-xs text-gray-600">Details: </span>
                                      <span className="text-xs">{entity.remarks}</span>
                                    </div>
                                  )}
                                </div>
                              </div>
                            ))}
                          </div>
                        </AlertDescription>
                      </Alert>
                    )}
                  </div>

                  <div className="flex flex-col items-end gap-2">
                    {getStatusBadge(result.status, result.hasMatch)}
                    <div className="flex gap-2">
                      <Button variant="outline" size="sm">
                        Review
                      </Button>
                      {result.hasMatch && (
                        <Button variant="outline" size="sm">
                          Investigate
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
            ))
          )}
        </div>
      </CardContent>
    </Card>
  )
}

// Server-side data for OFAC screening page
import { auth } from '@clerk/nextjs/server'

/**
 * ScreeningResult interface matching database schema
 * Note: matchedEntities is a JSONB field that may be null/undefined or have incomplete data
 */
interface ScreeningResult {
  id: string
  merchantName: string | null
  businessType: string | null
  hasMatch: boolean
  matchScore: string | number | null
  status: 'completed' | 'pending' | 'failed'
  checkedAt: string
  // JSONB field from database - needs defensive handling
  matchedEntities?: Array<{
    primaryName: string
    entityType: string
    programs?: string[] | null
    lists?: string[] | null
    matchScore: number
    aliases?: string[] | null
    remarks?: string | null
  }> | null
}

// Helper function to get API base URL
function getApiBaseUrl(): string {
  if (process.env['API_BASE_URL']) {
    return process.env['API_BASE_URL']
  }

  // Development fallback
  if (process.env['NODE_ENV'] === 'development') {
    return 'http://localhost:3001'
  }

  // Production should always have proper environment variables set
  throw new Error('API_BASE_URL must be configured in production')
}

// Fetch recent screening results from the API
export async function getScreeningResults(limit: number = 10): Promise<ScreeningResult[]> {
  try {
    const { userId, getToken } = await auth()

    // If not authenticated, return empty array (or mock data for development)
    if (!userId) {
      console.warn('No user authenticated - returning empty screening results')
      return []
    }

    // Get the secure JWT access token from Clerk
    const accessToken = await getToken()
    if (!accessToken) {
      console.error('No access token from Clerk')
      return []
    }

    const baseURL = getApiBaseUrl()
    const controller = new AbortController()
    const timeoutId = setTimeout(() => controller.abort(), 10000) // 10s timeout

    const response = await fetch(`${baseURL}/api/ofac/screening/recent?limit=${limit}`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${accessToken}`,
      },
      cache: 'no-store', // Always fetch fresh data
      signal: controller.signal,
    })

    clearTimeout(timeoutId)

    if (!response.ok) {
      console.error('Failed to fetch screening results:', response.status, response.statusText)
      return []
    }

    const data = await response.json()

    if (!data.success || !data.data) {
      console.error('Invalid response from screening API:', data)
      return []
    }

    // Transform API response to match component interface with defensive data handling
    return data.data.map((result: any) => {
      // Safely process matchedEntities JSONB field
      let safeMatchedEntities = undefined
      if (result.matchedEntities && Array.isArray(result.matchedEntities)) {
        safeMatchedEntities = result.matchedEntities.map((entity: any) => ({
          primaryName: entity.primaryName || 'Unknown',
          entityType: entity.entityType || 'Unknown',
          // Ensure arrays are always arrays or null, never undefined malformed data
          programs: Array.isArray(entity.programs) ? entity.programs : null,
          lists: Array.isArray(entity.lists) ? entity.lists : null,
          matchScore: typeof entity.matchScore === 'number' ? entity.matchScore : 0,
          aliases: Array.isArray(entity.aliases) ? entity.aliases : null,
          remarks: entity.remarks || null,
        }))
      }

      return {
        id: result.id,
        merchantName: result.merchantName || 'Unknown Merchant',
        businessType: result.businessType || 'Unknown',
        hasMatch: result.hasMatch || false,
        matchScore: typeof result.matchScore === 'string' ? parseFloat(result.matchScore) : (result.matchScore || 0),
        status: result.status === 'completed' ? 'completed' :
                result.status === 'pending' ? 'pending' : 'failed',
        checkedAt: result.checkedAt || new Date().toISOString(),
        matchedEntities: safeMatchedEntities,
      }
    })
  } catch (error) {
    // Handle timeout or network errors
    if (error instanceof Error && error.name === 'AbortError') {
      console.error('Request timeout fetching screening results')
    } else {
      console.error('Error fetching screening results:', error)
    }

    // Return empty array on error to prevent page crashes
    return []
  }
}

