import {
  pgTable,
  serial,
  varchar,
  timestamp,
  integer,
  jsonb,
  pgEnum,
  text,
  boolean,
  decimal,
  uuid,
  date,
  index,
  uniqueIndex
} from "drizzle-orm/pg-core";
import { partners, users, organizations } from "./schema";
import { sql } from "drizzle-orm";

// Simplified Enums - removed complex monitoring enums
export const merchantStatusEnum = pgEnum("merchant_status", ["pending", "active", "suspended", "terminated"]);
export const riskStatusEnum = pgEnum("risk_status", ["pending", "low", "medium", "high", "critical", "blocked"]);
export const ofacCheckTypeEnum = pgEnum("ofac_check_type", ["initial", "monitoring", "manual"]);
export const ofacCheckStatusEnum = pgEnum("ofac_check_status", ["pending", "completed", "failed"]);

// Main merchant table - simplified
export const merchants = pgTable("merchants", {
  id: uuid("id").defaultRandom().primaryKey(),
  externalId: varchar("external_id", { length: 255 }).unique(),
  organizationId: integer("organization_id").references(() => organizations.id),

  // ICA Tracking (Critical for ownership and access control)
  submittingIca: varchar("submitting_ica", { length: 50 }).notNull(),
  parentIca: varchar("parent_ica", { length: 50 }),
  submittedByUserId: integer("submitted_by_user_id").references(() => users.id),
  submissionDate: timestamp("submission_date", { withTimezone: true }).defaultNow(),

  // Business Information
  businessName: varchar("business_name", { length: 255 }).notNull(),
  dbaName: varchar("dba_name", { length: 255 }),
  taxId: varchar("tax_id", { length: 50 }),
  businessType: varchar("business_type", { length: 100 }),

  // Address Information
  addressLine1: varchar("address_line1", { length: 255 }),
  addressLine2: varchar("address_line2", { length: 255 }),
  city: varchar("city", { length: 100 }),
  state: varchar("state", { length: 50 }),
  postalCode: varchar("postal_code", { length: 20 }),
  country: varchar("country", { length: 2 }).default("US"),

  // Contact Information
  primaryContactName: varchar("primary_contact_name", { length: 255 }),
  primaryContactEmail: varchar("primary_contact_email", { length: 255 }),
  primaryContactPhone: varchar("primary_contact_phone", { length: 50 }),

  // Simplified Risk Information
  riskStatus: riskStatusEnum("risk_status").default("pending"),

  // Status
  onboardingStatus: merchantStatusEnum("onboarding_status").default("pending"),
  requiresReview: boolean("requires_review").default(false),

  // Timestamps
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow(),

  // Metadata
  metadata: jsonb("metadata")
}, (table) => {
  return {
    orgIdIdx: index("idx_merchants_org_id").on(table.organizationId),
    riskStatusIdx: index("idx_merchants_risk_status").on(table.riskStatus),
    createdAtIdx: index("idx_merchants_created_at").on(table.createdAt),
    submittingIcaIdx: index("idx_merchants_submitting_ica").on(table.submittingIca),
    businessNameIdx: index("idx_merchants_business_name").on(table.businessName),
  }
});

// Merchant principals/owners table - simplified
export const merchantPrincipals = pgTable("merchant_principals", {
  id: uuid("id").defaultRandom().primaryKey(),
  merchantId: uuid("merchant_id").references(() => merchants.id, { onDelete: "cascade" }),

  // Personal Information
  firstName: varchar("first_name", { length: 100 }).notNull(),
  middleName: varchar("middle_name", { length: 100 }),
  lastName: varchar("last_name", { length: 100 }).notNull(),
  dateOfBirth: date("date_of_birth"),

  // Address
  addressLine1: varchar("address_line1", { length: 255 }),
  addressLine2: varchar("address_line2", { length: 255 }),
  city: varchar("city", { length: 100 }),
  state: varchar("state", { length: 50 }),
  postalCode: varchar("postal_code", { length: 20 }),
  country: varchar("country", { length: 2 }).default("US"),

  // Ownership
  ownershipPercentage: decimal("ownership_percentage", { precision: 5, scale: 2 }),
  isControlOwner: boolean("is_control_owner").default(false),
  title: varchar("title", { length: 100 }),

  // Timestamps
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow()
}, (table) => {
  return {
    merchantIdIdx: index("idx_principals_merchant_id").on(table.merchantId),
  }
});

// OFAC screening results (matches actual database schema)
// Note: Using existing database structure, not simplified schema
export const ofacScreeningResults = pgTable("ofac_checks", {
  id: uuid("id").defaultRandom().primaryKey(),
  merchantId: uuid("merchant_id").references(() => merchants.id, { onDelete: "cascade" }),
  checkType: ofacCheckTypeEnum("check_type").notNull(),
  checkDate: timestamp("check_date", { withTimezone: true }).defaultNow(),

  // Business information (for ad-hoc screenings without merchant_id)
  businessName: varchar("business_name", { length: 255 }),
  businessType: varchar("business_type", { length: 100 }),

  // Check results (actual database columns)
  hasMatch: boolean("has_match").default(false),
  matchScore: decimal("match_score", { precision: 5, scale: 2 }),
  matchedEntities: jsonb("matched_entities"), // Actual column name in DB
  rawResponse: jsonb("raw_response"),

  // Monitoring
  monitoringEnabled: boolean("monitoring_enabled").default(true),
  monitoringFrequency: varchar("monitoring_frequency", { length: 50 }),
  nextCheckDate: timestamp("next_check_date", { withTimezone: true }),

  // Status tracking (actual database enum)
  status: ofacCheckStatusEnum("status").default("completed"),
  errorMessage: text("error_message"),
  processingTimeMs: integer("processing_time_ms"),

  // Audit (nullable for Clerk integration)
  performedBy: varchar("performed_by", { length: 255 }),
  organizationId: integer("organization_id").references(() => organizations.id),

  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow(),
  updatedAt: timestamp("updated_at", { withTimezone: true }).defaultNow()
}, (table) => {
  return {
    merchantIdIdx: index("idx_ofac_screening_merchant_id").on(table.merchantId),
    statusIdx: index("idx_ofac_screening_status").on(table.status),
    hasMatchIdx: index("idx_ofac_screening_has_match").on(table.hasMatch),
    businessNameIdx: index("idx_ofac_checks_business_name").on(table.businessName),
  }
});

// Simplified OFAC sanctions list cache
export const ofacSanctionsCache = pgTable("ofac_sanctions_cache", {
  id: uuid("id").defaultRandom().primaryKey(),
  entityId: varchar("entity_id", { length: 100 }).unique(),
  entityType: varchar("entity_type", { length: 50 }),
  primaryName: varchar("primary_name", { length: 500 }),
  aliases: text("aliases").array(),
  programs: text("programs").array(),
  lists: text("lists").array(),
  addresses: jsonb("addresses"),
  identifiers: jsonb("identifiers"),
  remarks: text("remarks"),

  // Metadata
  sourceUpdatedAt: timestamp("source_updated_at", { withTimezone: true }),
  cachedAt: timestamp("cached_at", { withTimezone: true }).defaultNow(),

  // Search optimization
  searchVector: text("search_vector")
}, (table) => {
  return {
    entityIdIdx: uniqueIndex("idx_ofac_sanctions_cache_entity_id").on(table.entityId),
    primaryNameIdx: index("idx_ofac_sanctions_cache_primary_name").on(table.primaryName),
    searchVectorIdx: index("idx_ofac_sanctions_cache_search_vector").on(table.searchVector),
  }
});

// Legacy table mapping for backward compatibility
// Maps old ofacChecks table structure to new simplified structure
export const ofacChecks = ofacScreeningResults;

import { db } from '../../db/index.js';
import { ofacSanctionsCache, ofacScreeningResults } from '../../db/schema-ofac.js';
import { logger } from '../../utils/logger.js';
import { eq, or, ilike, sql } from 'drizzle-orm';
import type { NeonHttpDatabase } from 'drizzle-orm/neon-http';

export interface ScreeningRequest {
  merchantId: string;
  searchType?: 'business' | 'individual';

  // Business fields
  businessName?: string;
  businessType?: string;

  // Individual fields
  individualName?: string;
  dateOfBirth?: string;
  placeOfBirth?: string;
  nationality?: string;
  citizenship?: string;

  // Common fields
  country?: string;
  additionalNames?: string[];
  addresses?: Array<{
    street: string;
    city: string;
    state?: string;
    postalCode?: string;
    country: string;
  }>;
  identifiers?: Array<{
    type: string;
    value: string;
    country?: string;
  }>;

  // Legacy support
  principals?: Principal[];
}

export interface Principal {
  id: string;
  firstName: string;
  middleName?: string;
  lastName: string;
}

export interface ScreeningResult {
  merchantId: string;
  businessName: string;
  status: 'CLEAR' | 'POTENTIAL_MATCH' | 'BLOCKED';
  hasMatch: boolean;
  matches: ScreeningMatch[];
  principalResults: PrincipalResult[];
  screenedAt: Date;
}

export interface ScreeningMatch {
  entityId: string;
  entityType: string;
  primaryName: string;
  programs: string[];
  matchType: 'EXACT' | 'ALIAS';
  matchedText: string;
}

export interface PrincipalResult {
  principalId: string;
  principalName: string;
  status: 'CLEAR' | 'POTENTIAL_MATCH' | 'BLOCKED';
  matches: ScreeningMatch[];
}

/**
 * Simplified OFAC Screening Service
 * - Exact match only (case-insensitive, normalized)
 * - Simple status: CLEAR, POTENTIAL_MATCH, BLOCKED
 * - No fuzzy matching algorithms
 * - Manual review for potential matches
 */
export class OFACScreeningService {

  /**
   * Screen a merchant/individual against OFAC sanctions list
   * Supports flexible search with multiple criteria
   * Uses batch query optimization to avoid N+1 pattern
   * Uses transaction to ensure screening result is stored atomically
   */
  async screenMerchant(request: ScreeningRequest): Promise<ScreeningResult> {
    logger.info(`Starting OFAC screening for: ${request.merchantId}`);

    try {
      // Prepare all names for batch screening
      const namesToScreen: string[] = [];
      const principalNameMap: Map<string, Principal> = new Map();

      // Determine search type and primary name
      const searchType = request.searchType || 'business';
      let primaryName = '';

      if (searchType === 'business' && request.businessName) {
        primaryName = request.businessName;
        namesToScreen.push(request.businessName);
      } else if (searchType === 'individual' && request.individualName) {
        primaryName = request.individualName;
        namesToScreen.push(request.individualName);
      }

      // Add additional names if provided
      if (request.additionalNames) {
        for (const name of request.additionalNames) {
          if (name && name.trim().length >= 2) {
            namesToScreen.push(name);
          }
        }
      }

      // Add principals for legacy support
      if (request.principals) {
        for (const principal of request.principals) {
          const principalName = this.formatPrincipalName(principal);
          namesToScreen.push(principalName);
          principalNameMap.set(principalName, principal);
        }
      }

      // Batch screen all names in a single query
      const allResults = await this.screenNames(namesToScreen);

      // Extract business/individual matches (first result)
      const businessMatches = allResults[0] || [];

      // Search by other criteria (date of birth, nationality, identifiers, addresses)
      const additionalMatches = await this.screenByAdditionalCriteria(request);

      // Build principal results from remaining matches
      const principalResults: PrincipalResult[] = [];
      if (request.principals) {
        for (let i = 0; i < request.principals.length; i++) {
          const principal = request.principals[i];
          const principalName = this.formatPrincipalName(principal);
          const principalMatches = allResults[i + 1] || []; // +1 because business is index 0

          principalResults.push({
            principalId: principal.id,
            principalName,
            status: this.determineStatus(principalMatches),
            matches: principalMatches
          });
        }
      }

      // Combine all matches (including additional criteria matches)
      const allMatches = [
        ...businessMatches,
        ...additionalMatches,
        ...principalResults.flatMap(pr => pr.matches)
      ];

      // Remove duplicates based on entityId
      const uniqueMatches = Array.from(
        new Map(allMatches.map(match => [match.entityId, match])).values()
      );

      const result: ScreeningResult = {
        merchantId: request.merchantId,
        businessName: primaryName || request.businessName || 'Unknown',
        status: this.determineOverallStatus(uniqueMatches, principalResults),
        hasMatch: uniqueMatches.length > 0,
        matches: uniqueMatches,
        principalResults,
        screenedAt: new Date()
      };

      // Use transaction to ensure screening result storage is atomic
      await db.transaction(async (tx: any) => {
        // Use raw SQL to avoid Drizzle's automatic DEFAULT insertion for all columns
        // This allows database defaults to work properly for nullable audit fields
        // Note: merchant_id is omitted for ad-hoc screenings (will be NULL in database)
        // Store business_name and business_type directly for proper display
        await tx.execute(sql`
          INSERT INTO ofac_checks (
            check_type,
            check_date,
            business_name,
            business_type,
            has_match,
            matched_entities,
            raw_response,
            status
          ) VALUES (
            'initial'::ofac_check_type,
            ${result.screenedAt.toISOString()}::timestamptz,
            ${request.businessName || request.individualName || null},
            ${request.businessType || 'unknown'},
            ${result.hasMatch}::boolean,
            ${sql.raw(`'${JSON.stringify(result.matches)}'::jsonb`)},
            ${sql.raw(`'${JSON.stringify({
              status: result.status,
              principalResults: result.principalResults,
              merchantId: result.merchantId // Store for reference
            })}'::jsonb`)},
            'completed'::ofac_check_status
          )
        `);

        logger.info(`Stored OFAC screening result for ad-hoc screening ${result.merchantId}`);
      });

      logger.info(`OFAC screening completed for ${request.merchantId}: ${result.status}`);
      return result;

    } catch (error) {
      logger.error(`OFAC screening failed for merchant ${request.merchantId}:`, error);
      throw error;
    }
  }

  /**
   * Screen multiple names in a single batch query (fixes N+1 pattern)
   * Returns array of matches in same order as input names
   *
   * Performance: Reduces 1+N queries to 1 query for screening merchant + principals
   * Expected improvement: 10x faster for merchants with 10 principals
   */
  async screenNames(names: string[]): Promise<ScreeningMatch[][]> {
    if (!names || names.length === 0) {
      return [];
    }

    // Filter and normalize all names
    const normalizedNames = names
      .filter(name => name && name.trim().length >= 2)
      .map(name => this.normalizeName(name));

    if (normalizedNames.length === 0) {
      return names.map(() => []);
    }

    try {
      // Build OR conditions for all names in a single query
      // Uses GIN indexes from migration 0009 for fast ILIKE operations
      const orConditions = normalizedNames.flatMap(normalizedName => [
        ilike(ofacSanctionsCache.primaryName, normalizedName),
        sql`${normalizedName} = ANY(${ofacSanctionsCache.aliases})`
      ]);

      // Single database query for all names
      const entities = await db
        .select()
        .from(ofacSanctionsCache)
        .where(or(...orConditions));

      // Group matches by original name
      const resultsByName = new Map<string, ScreeningMatch[]>();

      // Initialize empty arrays for all names
      for (const normalizedName of normalizedNames) {
        resultsByName.set(normalizedName, []);
      }

      // Process each entity and match it to the correct name(s)
      for (const entity of entities) {
        for (const normalizedName of normalizedNames) {
          // Check exact match in primary name
          if (this.isExactMatch(normalizedName, entity.primaryName)) {
            resultsByName.get(normalizedName)!.push({
              entityId: entity.entityId,
              entityType: entity.entityType,
              primaryName: entity.primaryName,
              programs: entity.programs || [],
              matchType: 'EXACT',
              matchedText: entity.primaryName
            });
          }

          // Check exact match in aliases
          if (entity.aliases) {
            for (const alias of entity.aliases) {
              if (this.isExactMatch(normalizedName, alias)) {
                resultsByName.get(normalizedName)!.push({
                  entityId: entity.entityId,
                  entityType: entity.entityType,
                  primaryName: entity.primaryName,
                  programs: entity.programs || [],
                  matchType: 'ALIAS',
                  matchedText: alias
                });
              }
            }
          }
        }
      }

      // Return results in same order as input names
      return normalizedNames.map(name => resultsByName.get(name) || []);

    } catch (error) {
      logger.error(`Failed to batch screen names:`, error);
      // Return empty arrays for all names on error
      return names.map(() => []);
    }
  }

  /**
   * Screen by additional criteria (DOB, nationality, identifiers, addresses)
   * Searches across JSONB fields in ofac_sanctions_cache
   */
  private async screenByAdditionalCriteria(request: ScreeningRequest): Promise<ScreeningMatch[]> {
    const matches: ScreeningMatch[] = [];

    try {
      const conditions: any[] = [];

      // Search by date of birth (for individuals)
      if (request.dateOfBirth) {
        conditions.push(
          sql`${ofacSanctionsCache.identifiers}::text ILIKE ${`%${request.dateOfBirth}%`}`
        );
      }

      // Search by nationality
      if (request.nationality) {
        const normalized = this.normalizeName(request.nationality);
        conditions.push(
          sql`${ofacSanctionsCache.addresses}::text ILIKE ${`%${normalized}%`}`
        );
      }

      // Search by citizenship
      if (request.citizenship) {
        const normalized = this.normalizeName(request.citizenship);
        conditions.push(
          sql`${ofacSanctionsCache.addresses}::text ILIKE ${`%${normalized}%`}`
        );
      }

      // Search by place of birth
      if (request.placeOfBirth) {
        const normalized = this.normalizeName(request.placeOfBirth);
        conditions.push(
          sql`${ofacSanctionsCache.addresses}::text ILIKE ${`%${normalized}%`}`
        );
      }

      // Search by identifiers (passport, national ID, SSN, etc.)
      if (request.identifiers && request.identifiers.length > 0) {
        for (const identifier of request.identifiers) {
          if (identifier.value && identifier.value.trim().length >= 3) {
            conditions.push(
              sql`${ofacSanctionsCache.identifiers}::text ILIKE ${`%${identifier.value}%`}`
            );
          }
        }
      }

      // Search by addresses
      if (request.addresses && request.addresses.length > 0) {
        for (const address of request.addresses) {
          const searchTerms = [
            address.street,
            address.city,
            address.state,
            address.postalCode,
            address.country
          ].filter(Boolean).join(' ');

          if (searchTerms.length >= 5) {
            const normalized = this.normalizeName(searchTerms);
            conditions.push(
              sql`${ofacSanctionsCache.addresses}::text ILIKE ${`%${normalized}%`}`
            );
          }
        }
      }

      // If no additional criteria, return empty array
      if (conditions.length === 0) {
        return [];
      }

      // Execute search with OR conditions
      const entities = await db
        .select()
        .from(ofacSanctionsCache)
        .where(or(...conditions));

      // Convert entities to screening matches
      for (const entity of entities) {
        matches.push({
          entityId: entity.entityId,
          entityType: entity.entityType,
          primaryName: entity.primaryName,
          programs: entity.programs || [],
          matchType: 'EXACT',
          matchedText: entity.primaryName
        });
      }

      return matches;

    } catch (error) {
      logger.error('Failed to screen by additional criteria:', error);
      return [];
    }
  }

  /**
   * Screen a single name against OFAC sanctions list
   * Uses exact matching only (case-insensitive, normalized)
   *
   * Note: For screening multiple names, use screenNames() for better performance
   */
  private async screenName(name: string): Promise<ScreeningMatch[]> {
    if (!name || name.trim().length < 2) {
      return [];
    }

    const normalizedName = this.normalizeName(name);
    const matches: ScreeningMatch[] = [];

    try {
      // Search for exact matches in primary names and aliases
      const entities = await db
        .select()
        .from(ofacSanctionsCache)
        .where(
          or(
            ilike(ofacSanctionsCache.primaryName, normalizedName),
            sql`${normalizedName} = ANY(${ofacSanctionsCache.aliases})`
          )
        );

      for (const entity of entities) {
        // Check exact match in primary name
        if (this.isExactMatch(normalizedName, entity.primaryName)) {
          matches.push({
            entityId: entity.entityId,
            entityType: entity.entityType,
            primaryName: entity.primaryName,
            programs: entity.programs || [],
            matchType: 'EXACT',
            matchedText: entity.primaryName
          });
        }

        // Check exact match in aliases
        if (entity.aliases) {
          for (const alias of entity.aliases) {
            if (this.isExactMatch(normalizedName, alias)) {
              matches.push({
                entityId: entity.entityId,
                entityType: entity.entityType,
                primaryName: entity.primaryName,
                programs: entity.programs || [],
                matchType: 'ALIAS',
                matchedText: alias
              });
            }
          }
        }
      }

      return matches;

    } catch (error) {
      logger.error(`Failed to screen name "${name}":`, error);
      return [];
    }
  }

  /**
   * Normalize name for exact matching
   * - Convert to lowercase
   * - Remove extra spaces
   * - Remove special characters
   */
  private normalizeName(name: string): string {
    return name
      .toLowerCase()
      .trim()
      .replace(/[^\w\s]/g, ' ') // Replace special chars with spaces
      .replace(/\s+/g, ' ') // Collapse multiple spaces
      .trim();
  }

  /**
   * Check if two normalized names are an exact match
   */
  private isExactMatch(name1: string, name2: string): boolean {
    return this.normalizeName(name1) === this.normalizeName(name2);
  }

  /**
   * Format principal name for screening
   */
  private formatPrincipalName(principal: Principal): string {
    const parts = [
      principal.firstName,
      principal.middleName,
      principal.lastName
    ].filter(Boolean);

    return parts.join(' ').trim();
  }

  /**
   * Determine status based on matches
   */
  private determineStatus(matches: ScreeningMatch[]): 'CLEAR' | 'POTENTIAL_MATCH' | 'BLOCKED' {
    if (matches.length === 0) {
      return 'CLEAR';
    }

    // For MVP, any exact match requires manual review
    // In production, you might have rules to auto-block certain programs
    return 'POTENTIAL_MATCH';
  }

  /**
   * Determine overall status considering all matches and principal results
   */
  private determineOverallStatus(
    allMatches: ScreeningMatch[],
    principalResults: PrincipalResult[]
  ): 'CLEAR' | 'POTENTIAL_MATCH' | 'BLOCKED' {
    // If any matches found, needs review
    if (allMatches.length > 0) {
      return 'POTENTIAL_MATCH';
    }

    // If any principal has matches, needs review
    const principalMatch = principalResults.some(pr => pr.matches.length > 0);
    if (principalMatch) {
      return 'POTENTIAL_MATCH';
    }

    return 'CLEAR';
  }

  /**
   * Store screening result in database
   * Note: This method is deprecated - use screenMerchant() which handles storage within a transaction
   * @deprecated Use screenMerchant() instead
   */
  private async storeScreeningResult(result: ScreeningResult): Promise<void> {
    try {
      await db.insert(ofacScreeningResults).values({
        merchantId: result.merchantId,
        checkType: 'initial',
        checkDate: result.screenedAt,
        hasMatch: result.hasMatch,
        matchedEntities: result.matches,
        rawResponse: {
          status: result.status,
          principalResults: result.principalResults
        },
        status: 'completed'
        // Note: performedBy and organizationId omitted (will use database defaults/null)
      });

      logger.info(`Stored OFAC screening result for merchant ${result.merchantId}`);

    } catch (error) {
      logger.error(`Failed to store screening result for merchant ${result.merchantId}:`, error);
      throw error; // Throw to trigger transaction rollback
    }
  }

  /**
   * Get screening history for a merchant
   */
  async getScreeningHistory(merchantId: string) {
    try {
      return await db
        .select()
        .from(ofacScreeningResults)
        .where(eq(ofacScreeningResults.merchantId, merchantId))
        .orderBy(sql`${ofacScreeningResults.checkDate} DESC`)
        .limit(10);

    } catch (error) {
      logger.error(`Failed to get screening history for merchant ${merchantId}:`, error);
      return [];
    }
  }
}

