    value: z.string(),
    country: z.string().optional(),
  })).optional(),

  // Legacy support
  principals: z.array(z.object({
    id: z.string(),
    firstName: z.string().min(1),
    middleName: z.string().optional(),
    lastName: z.string().min(1)
  })).optional()
});

const syncSchema = z.object({
  force: z.boolean().optional()
});

export default async function ofacRoutes(server: FastifyInstance) {
  // Rate limiting for OFAC operations
  const screeningRateLimit = {
    config: {
      rateLimit: {
        max: 20,
        timeWindow: '1 minute'
      }
    }
  };

  const adminRateLimit = {
    config: {
      rateLimit: {
        max: 5,
        timeWindow: '1 minute'
      }
    }
  };

  // Initialize OFAC service on first request
  let initialized = false;
  const ensureInitialized = async () => {
    if (!initialized) {
      await ofacService.initialize();
      initialized = true;
    }
  };

  // === Core Screening Endpoints ===

  /**
   * POST /api/ofac/screen
   * Screen a merchant and their principals
   */
  server.post('/api/ofac/screen', screeningRateLimit, async (request: FastifyRequest, reply: FastifyReply) => {
    try {
      await ensureInitialized();

      const validatedData = screeningSchema.parse(request.body);

      // Generate merchantId if not provided (for ad-hoc screenings)
      const merchantId = validatedData.merchantId || crypto.randomUUID();

      const screeningRequest = {
        merchantId,
        businessName: validatedData.businessName,
        businessType: validatedData.businessType,
        principals: validatedData.principals
      };

      server.log.info('OFAC Screening Request:', { merchantId, businessName: validatedData.businessName });

      const result = await ofacService.screening.screenMerchant(screeningRequest);

      server.log.info('OFAC Screening Result:', { merchantId, status: result.status });

      return {
        success: true,
        data: result
      };
    } catch (error) {
      server.log.error('Error screening merchant:', error);
      if (error instanceof z.ZodError) {
        reply.status(400);
        return {
          success: false,
          error: 'Validation error',
          details: error.errors
        };
      } else {
        reply.status(500);
        return {
          success: false,
          error: error.message || 'Failed to screen merchant'
        };
      }
    }
  });

  /**
   * GET /api/ofac/screening/history/:merchantId
   * Get screening history for a merchant
   */
  server.get<{
    Params: { merchantId: string };
    Querystring: { limit?: string };
  }>('/api/ofac/screening/history/:merchantId', async (request, reply) => {
    try {
      const { merchantId } = request.params;
      const limit = parseInt(request.query.limit || '10');

      const history = await ofacService.screening.getScreeningHistory(merchantId);

      return {
        success: true,
        data: history.slice(0, limit)
      };
    } catch (error) {
      server.log.error('Error fetching screening history:', error);
      reply.status(500);
      return {
        success: false,
        error: error.message || 'Failed to fetch screening history'
      };
    }
  });

  /**
   * GET /api/ofac/screening/results/:resultId
   * Get specific screening result details
   */
  server.get<{ Params: { resultId: string } }>(
    '/api/ofac/screening/results/:resultId',
    async (request, reply) => {
      try {
        const { resultId } = request.params;

        const result = await db.select()
          .from(ofacScreeningResults)
          .where(eq(ofacScreeningResults.id, resultId))
          .limit(1);

        if (!result || result.length === 0) {
          reply.status(404);
          return {
            success: false,
            error: 'Screening result not found'
          };
        }

        return {
          success: true,
          data: result[0]
        };
      } catch (error) {
        server.log.error('Error fetching screening result:', error);
        reply.status(500);
        return {
          success: false,
          error: error.message || 'Failed to fetch screening result'
        };
      }
    }
  );

  /**
   * GET /api/ofac/screening/recent
   * Get recent screening results (newest first)
   */
  server.get<{
    Querystring: { limit?: string; organizationId?: string };
  }>('/api/ofac/screening/recent', async (request, reply) => {
    try {
      const limit = parseInt(request.query.limit || '10');
      const organizationId = request.query.organizationId ? parseInt(request.query.organizationId) : undefined;

      // Build query - no need for JOIN since business info is stored in ofac_checks
      // For ad-hoc screenings: business_name and business_type are stored directly
      // For merchant-linked screenings: these fields duplicate the merchant table (optimization)
      let query = db.select({
        id: ofacScreeningResults.id,
        merchantId: ofacScreeningResults.merchantId,
        merchantName: ofacScreeningResults.businessName,
        businessType: ofacScreeningResults.businessType,
        hasMatch: ofacScreeningResults.hasMatch,
        matchScore: ofacScreeningResults.matchScore,
        status: ofacScreeningResults.status,
        checkedAt: ofacScreeningResults.checkDate,
        matchedEntities: ofacScreeningResults.matchedEntities,
      })
        .from(ofacScreeningResults)
        .orderBy(desc(ofacScreeningResults.checkDate))
        .limit(limit);

      // Apply organization filter if provided (requires JOIN to merchants)
      if (organizationId) {
        query = db.select({
          id: ofacScreeningResults.id,
          merchantId: ofacScreeningResults.merchantId,
          merchantName: ofacScreeningResults.businessName,
          businessType: ofacScreeningResults.businessType,
          hasMatch: ofacScreeningResults.hasMatch,
          matchScore: ofacScreeningResults.matchScore,
          status: ofacScreeningResults.status,
          checkedAt: ofacScreeningResults.checkDate,
          matchedEntities: ofacScreeningResults.matchedEntities,
        })
          .from(ofacScreeningResults)
          .leftJoin(merchants, eq(merchants.id, ofacScreeningResults.merchantId))
          .where(eq(merchants.organizationId, organizationId))
          .orderBy(desc(ofacScreeningResults.checkDate))
          .limit(limit) as typeof query;
      }

      const results = await query;

      return {
        success: true,
        data: results
      };
    } catch (error) {
      server.log.error('Error fetching recent screening results:', error);
      reply.status(500);
      return {
        success: false,
        error: error.message || 'Failed to fetch recent screening results'
      };
    }
  });

  // === Data Sync Endpoints (Admin) ===

  /**
   * POST /api/ofac/sync
   * Trigger OFAC data sync
   */
  server.post('/api/ofac/sync', adminRateLimit, async (request: FastifyRequest, reply: FastifyReply) => {
    try {
      const validatedData = syncSchema.parse(request.body || {});

      // Check current sync status
      const currentStatus = await ofacService.sync.getSyncStatus();

      if (!validatedData.force && currentStatus.hasData) {
        return {
          success: true,
          message: 'OFAC data already synced. Use force=true to re-sync.',
          data: currentStatus
        };
      }

      const result = await ofacService.sync.performFullSync();

      return {
        success: true,
        data: result
      };
    } catch (error) {
      server.log.error('Error syncing OFAC data:', error);
      reply.status(500);
      return {
        success: false,
        error: error.message || 'Failed to sync OFAC data'
      };
    }
  });

  /**
   * GET /api/ofac/sync/status
   * Get current sync status
   */
  server.get('/api/ofac/sync/status', async (request, reply) => {
    try {
      const status = await ofacService.sync.getSyncStatus();

      return {
        success: true,
        data: status
      };
    } catch (error) {
      server.log.error('Error fetching sync status:', error);
      reply.status(500);
      return {
        success: false,
        error: error.message || 'Failed to fetch sync status'
      };
    }
  });

  // === Statistics & Reporting ===

  /**
   * GET /api/ofac/stats
   * Get screening statistics
   */
  server.get<{
    Querystring: { period?: string };
  }>('/api/ofac/stats', async (request, reply) => {
    try {
      const period = request.query.period || '30d';

      // Calculate date range
      const endDate = new Date();
      const startDate = new Date();
      switch (period) {
        case '7d':
          startDate.setDate(startDate.getDate() - 7);
          break;
        case '30d':
          startDate.setDate(startDate.getDate() - 30);
          break;
        case '90d':
          startDate.setDate(startDate.getDate() - 90);
          break;
        default:
          startDate.setDate(startDate.getDate() - 30);
      }

      // Get statistics
      const stats = await db.select({
        totalScreenings: sql<number>`COUNT(*)`,
        clearCount: sql<number>`COUNT(*) FILTER (WHERE ${ofacScreeningResults.status} = 'CLEAR')`,
        potentialMatchCount: sql<number>`COUNT(*) FILTER (WHERE ${ofacScreeningResults.status} = 'POTENTIAL_MATCH')`,
        blockedCount: sql<number>`COUNT(*) FILTER (WHERE ${ofacScreeningResults.status} = 'BLOCKED')`,
        matchedCount: sql<number>`COUNT(*) FILTER (WHERE ${ofacScreeningResults.hasMatch} = true)`
      })
        .from(ofacScreeningResults)
        .where(
          and(
            gte(ofacScreeningResults.checkDate, startDate),
            lte(ofacScreeningResults.checkDate, endDate)
          )
        );

      return {
        success: true,
        data: {
          period,
          startDate,
          endDate,
          statistics: stats[0]
        }
      };
    } catch (error) {
      server.log.error('Error fetching OFAC statistics:', error);
      reply.status(500);
      return {
        success: false,
        error: error.message || 'Failed to fetch OFAC statistics'
      };
    }
  });

  /**
   * GET /api/ofac/merchants/review
   * Get merchants requiring manual review
   */
  server.get('/api/ofac/merchants/review', async (request, reply) => {
    try {
      const results = await db.select({
        id: ofacScreeningResults.id,
        merchantId: ofacScreeningResults.merchantId,
        businessName: merchants.businessName,
        status: ofacScreeningResults.status,
        hasMatch: ofacScreeningResults.hasMatch,
        matches: ofacScreeningResults.matches,
        checkDate: ofacScreeningResults.checkDate
      })
        .from(ofacScreeningResults)
        .innerJoin(merchants, eq(merchants.id, ofacScreeningResults.merchantId))
        .where(eq(ofacScreeningResults.status, 'POTENTIAL_MATCH'))
        .orderBy(desc(ofacScreeningResults.checkDate))
        .limit(50);

      return {
        success: true,
        data: results
      };
    } catch (error) {
      server.log.error('Error fetching merchants requiring review:', error);
      reply.status(500);
      return {
        success: false,
        error: error.message || 'Failed to fetch merchants requiring review'
      };
    }
  });

  // === Health Check ===

  /**
   * GET /api/ofac/health
   * Health check for OFAC service
   */
  server.get('/api/ofac/health', async (request, reply) => {
    try {
      const health = await ofacService.healthCheck();

      return {
        success: true,
        status: 'ok',
        service: 'OFAC Screening (Simplified)',
        timestamp: new Date().toISOString(),
        data: health
      };
    } catch (error) {
      server.log.error('Error in OFAC health check:', error);
      reply.status(500);
      return {
        success: false,
        status: 'error',
        service: 'OFAC Screening (Simplified)',
        timestamp: new Date().toISOString(),
        error: error.message
      };
    }
  });
}

