// Fetch recent screening results from the API
export async function getScreeningResults(limit: number = 10): Promise<ScreeningResult[]> {
  try {
    const { userId, getToken } = await auth()

    // If not authenticated, return empty array (or mock data for development)
    if (!userId) {
      console.warn('No user authenticated - returning empty screening results')
      return []
    }

    // Get the secure JWT access token from Clerk
    const accessToken = await getToken()
    if (!accessToken) {
      console.error('No access token from Clerk')
      return []
    }

    const baseURL = getApiBaseUrl()
    const controller = new AbortController()
    const timeoutId = setTimeout(() => controller.abort(), 10000) // 10s timeout

    const response = await fetch(`${baseURL}/api/ofac/screening/recent?limit=${limit}`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${accessToken}`,
      },
      cache: 'no-store', // Always fetch fresh data
      signal: controller.signal,
    })

    clearTimeout(timeoutId)

    if (!response.ok) {
      console.error('Failed to fetch screening results:', response.status, response.statusText)
      return []
    }

    const data = await response.json()

    if (!data.success || !data.data) {
      console.error('Invalid response from screening API:', data)
      return []
    }

    // Transform API response to match component interface with defensive data handling
    return data.data.map((result: any) => {
      // Safely process matchedEntities JSONB field
      let safeMatchedEntities = undefined
      if (result.matchedEntities && Array.isArray(result.matchedEntities)) {
        safeMatchedEntities = result.matchedEntities.map((entity: any) => ({
          primaryName: entity.primaryName || 'Unknown',
          entityType: entity.entityType || 'Unknown',
          // Ensure arrays are always arrays or null, never undefined malformed data
          programs: Array.isArray(entity.programs) ? entity.programs : null,
          lists: Array.isArray(entity.lists) ? entity.lists : null,
          matchScore: typeof entity.matchScore === 'number' ? entity.matchScore : 0,
          aliases: Array.isArray(entity.aliases) ? entity.aliases : null,
          remarks: entity.remarks || null,
        }))
      }

      return {
        id: result.id,
        merchantName: result.merchantName || 'Unknown Merchant',
        businessType: result.businessType || 'Unknown',
        hasMatch: result.hasMatch || false,
        matchScore: typeof result.matchScore === 'string' ? parseFloat(result.matchScore) : (result.matchScore || 0),
        status: result.status === 'completed' ? 'completed' :
                result.status === 'pending' ? 'pending' : 'failed',
        checkedAt: result.checkedAt || new Date().toISOString(),
        matchedEntities: safeMatchedEntities,
      }
    })
  } catch (error) {
    // Handle timeout or network errors
    if (error instanceof Error && error.name === 'AbortError') {
      console.error('Request timeout fetching screening results')
    } else {
      console.error('Error fetching screening results:', error)
    }

    // Return empty array on error to prevent page crashes
    return []
  }
}

