import { drizzle } from 'drizzle-orm/neon-serverless';
import { neon } from '@neondatabase/serverless';
import dotenv from 'dotenv';
import { sql } from 'drizzle-orm';

dotenv.config();

const databaseUrl = process.env.DATABASE_URL;
if (!databaseUrl) {
  throw new Error('DATABASE_URL is required');
}

const connection = neon(databaseUrl);
const db = drizzle(connection);

async function addEntityTypeColumn() {
  console.log('Adding entity_type column to ofac_checks table...');

  try {
    // Add the column
    await db.execute(sql`ALTER TABLE ofac_checks ADD COLUMN IF NOT EXISTS entity_type VARCHAR(50)`);
    console.log('✓ Added entity_type column');

    // Update existing records
    await db.execute(sql`
      UPDATE ofac_checks
      SET entity_type = 'business'
      WHERE business_type IS NOT NULL AND business_type != 'unknown' AND entity_type IS NULL
    `);
    console.log('✓ Updated existing business records');

    await db.execute(sql`
      UPDATE ofac_checks
      SET entity_type = 'individual'
      WHERE entity_type IS NULL
    `);
    console.log('✓ Updated remaining records as individual');

    console.log('\n✅ Migration completed successfully!');
  } catch (error) {
    console.error('❌ Migration failed:', error);
    process.exit(1);
  }
}

addEntityTypeColumn();

