#!/usr/bin/env node

/**
 * Schema synchronization script
 * Copies validation schemas from api-server to frontend to maintain DRY principle
 *
 * This script:
 * 1. Copies schemas from api-server/src/schemas/ to src/lib/schemas/
 * 2. Updates import paths to work in the frontend context
 * 3. Preserves TypeScript types and exports
 * 4. Ensures single source of truth for validation logic
 */

const fs = require('fs');
const path = require('path');

const SOURCE_DIR = path.join(__dirname, '../api-server/src/schemas');
const DEST_DIR = path.join(__dirname, '../src/lib/schemas');

console.log('ðŸ”„ Starting schema synchronization...');
console.log(`ðŸ“ Source: ${SOURCE_DIR}`);
console.log(`ðŸ“ Destination: ${DEST_DIR}`);

// Ensure destination directory exists
if (!fs.existsSync(DEST_DIR)) {
  fs.mkdirSync(DEST_DIR, { recursive: true });
  console.log('âœ… Created destination directory');
}

// Clean existing schemas (except any frontend-specific ones we want to preserve)
const preserveFiles = ['.gitkeep', 'README.md'];
const existingFiles = fs.readdirSync(DEST_DIR);
for (const file of existingFiles) {
  if (!preserveFiles.includes(file)) {
    fs.unlinkSync(path.join(DEST_DIR, file));
    console.log(`ðŸ—‘ï¸  Removed existing file: ${file}`);
  }
}

// Get all TypeScript files from source directory
const sourceFiles = fs.readdirSync(SOURCE_DIR).filter(file => file.endsWith('.ts'));

console.log(`ðŸ“‹ Found ${sourceFiles.length} schema files to sync:`);
sourceFiles.forEach(file => console.log(`   - ${file}`));

// Copy and process each file
for (const file of sourceFiles) {
  const sourcePath = path.join(SOURCE_DIR, file);
  const destPath = path.join(DEST_DIR, file);

  try {
    let content = fs.readFileSync(sourcePath, 'utf8');

    // Transform import paths for frontend context
    // The schemas in api-server import from './security', './auth', etc.
    // In frontend, they should import from the same relative paths
    // No transformation needed since we're copying the entire directory structure

    // Add a header comment indicating this is a synced file
    const header = `// This file is automatically synced from api-server/src/schemas/${file}
// Do not edit this file directly - make changes in the api-server source
// Run 'npm run sync-schemas' to update

`;

    content = header + content;

    fs.writeFileSync(destPath, content);
    console.log(`âœ… Synced: ${file}`);

  } catch (error) {
    console.error(`âŒ Error syncing ${file}:`, error.message);
    process.exit(1);
  }
}

// Create a README in the destination directory
const readmeContent = `# Validation Schemas

This directory contains validation schemas that are automatically synchronized from the API server.

**âš ï¸ DO NOT EDIT FILES IN THIS DIRECTORY DIRECTLY**

These files are automatically generated by the \`sync-schemas\` script. To make changes:

1. Edit the source files in \`api-server/src/schemas/\`
2. Run \`npm run sync-schemas\` to update the frontend copies

## Architecture

This approach ensures:
- **Single Source of Truth**: Schemas are defined once in the API server
- **Type Safety**: Full TypeScript support across frontend and backend
- **Consistency**: Frontend and backend use identical validation logic
- **DRY Principle**: No duplicate schema definitions to maintain

## Available Schemas

- \`security.ts\` - Security utilities and base validators
- \`auth.ts\` - Authentication and user management schemas
- \`match-pro.ts\` - MATCH Pro API request/response validation
- \`ofac.ts\` - OFAC screening and monitoring schemas
- \`orc.ts\` - ORC risk assessment schemas
- \`index.ts\` - Exports all schemas for easy importing

## Usage

\`\`\`typescript
import { loginSchema, registrationSchema } from '@/lib/schemas'
import { terminationInquiryRequestSchema } from '@/lib/schemas'
import { ofacScreeningSchema } from '@/lib/schemas'
\`\`\`

Last synchronized: ${new Date().toISOString()}
`;

fs.writeFileSync(path.join(DEST_DIR, 'README.md'), readmeContent);

console.log('ðŸ“ Created README.md');
console.log(`âœ… Schema synchronization complete! Synced ${sourceFiles.length} files.`);
console.log('');
console.log('ðŸŽ¯ Next steps:');
console.log('   1. Update imports in your frontend code to use @/lib/schemas');
console.log('   2. Remove old validator files from src/lib/validators');
console.log('   3. Test the application to ensure everything works correctly');
console.log('');
console.log('ðŸ’¡ Remember: Always edit schemas in api-server/src/schemas/ and run sync-schemas');

