import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { AuthService } from './service.js';
import { JWTService } from './jwt.js';
import { z } from 'zod';

const loginSchema = z.object({
  email: z.string().email(),
  password: z.string().min(1)
});

const refreshTokenSchema = z.object({
  refreshToken: z.string().min(1)
});

type LoginRequest = FastifyRequest<{
  Body: z.infer<typeof loginSchema>
}>;

type RefreshTokenRequest = FastifyRequest<{
  Body: z.infer<typeof refreshTokenSchema>
}>;

export async function simpleAuthRoutes(server: FastifyInstance) {
  // Login endpoint with secure JWT token generation
  server.post('/api/auth/login', {
    schema: {
      body: {
        type: 'object',
        required: ['email', 'password'],
        properties: {
          email: { type: 'string', format: 'email' },
          password: { type: 'string', minLength: 1 }
        }
      }
    }
  }, async (request: LoginRequest, reply: FastifyReply) => {
    try {
      const { email, password } = request.body;

      // Rate limiting check (would integrate with rate limiting service)
      request.log.info('Login attempt', { email, ip: request.ip });

      // Authenticate user
      const user = await AuthService.authenticateUser(email, password);

      if (!user) {
        request.log.warn('Failed login attempt', {
          email,
          ip: request.ip,
          userAgent: request.headers['user-agent']

