import { z } from 'zod';
import {
  merchantUploadRowSchema,
  MerchantUploadRow,
  MerchantUploadRowInput,
  FormattedValidationError,
  ValidationResult,
  US_STATES
} from '../../schemas/merchantUpload';

/**
 * Field-level validator for merchant upload data
 *
 * Provides individual field validation methods that can be used
 * independently or as part of the full row validation.
 */
export class MerchantValidator {
  // =====================================================
  // Phone Validation
  // =====================================================

  /**
   * Validates phone number format
   *
   * Accepts: (888) 888-8888, 888-888-8888, or 8888888888
   * Returns: Digits only (10 digits)
   */
  validatePhone(phone: string): ValidationResult {
    if (!phone || phone.trim().length === 0) {
      return { valid: true }; // Phone is optional
    }

    const phoneRegex = /^(\(\d{3}\)\s?|\d{3}[-\s]?)?\d{3}[-\s]?\d{4}$/;

    if (!phoneRegex.test(phone)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'phone',
          column: '',
          value: phone,
          error: 'Invalid phone format. Expected: (888) 888-8888, 888-888-8888, or 8888888888',
          code: 'invalid_format'
        }]
      };
    }

    // Extract digits only
    const digitsOnly = phone.replace(/[^\d]/g, '');

    if (digitsOnly.length !== 10) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'phone',
          column: '',
          value: phone,
          error: 'Phone number must be exactly 10 digits',
          code: 'invalid_length'
        }]
      };
    }

    return { valid: true, data: digitsOnly as any };
  }

  // =====================================================
  // TIN Validation
  // =====================================================

  /**
   * Validates Tax Identification Number (TIN) format
   *
   * Accepts: 88-8888888 or 888888888 (9 digits)
   * Returns: Digits only (9 digits without dashes)
   */
  validateTin(tin: string): ValidationResult {
    if (!tin || tin.trim().length === 0) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'tin',
          column: '',
          value: tin,
          error: 'TIN is required',
          code: 'required'
        }]
      };
    }

    const tinRegex = /^\d{2}-?\d{7}$/;

    if (!tinRegex.test(tin)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'tin',
          column: '',
          value: tin,
          error: 'Invalid TIN format. Expected: 9 digits (88-8888888 or 888888888)',
          code: 'invalid_format'
        }]
      };
    }

    // Extract digits only
    const digitsOnly = tin.replace(/-/g, '');

    if (digitsOnly.length !== 9) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'tin',
          column: '',
          value: tin,
          error: 'TIN must be exactly 9 digits',
          code: 'invalid_length'
        }]
      };
    }

    return { valid: true, data: digitsOnly as any };
  }

  // =====================================================
  // SSN Validation
  // =====================================================

  /**
   * Validates Social Security Number (SSN) format
   *
   * Accepts: 9 digits (no dashes)
   * Returns: Digits only (9 digits)
   */
  validateSsn(ssn: string): ValidationResult {
    if (!ssn || ssn.trim().length === 0) {
      return { valid: true }; // SSN is optional in most contexts
    }

    const ssnRegex = /^\d{9}$/;

    if (!ssnRegex.test(ssn)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'ssn',
          column: '',
          value: ssn,
          error: 'Invalid SSN format. Expected: 9 digits (no dashes)',
          code: 'invalid_format'
        }]
      };
    }

    // Check for invalid SSN patterns
    // SSN cannot be all zeros, all same digit, or known invalid ranges
    const invalidPatterns = [
      /^000/, // Area number 000
      /^666/, // Area number 666
      /^9/, // Area numbers starting with 9
      /^(\d)\1{8}$/, // All same digit (e.g., 111111111)
    ];

    for (const pattern of invalidPatterns) {
      if (pattern.test(ssn)) {
        return {
          valid: false,
          errors: [{
            row: 0,
            field: 'ssn',
            column: '',
            value: ssn,
            error: 'Invalid SSN pattern',
            code: 'invalid_pattern'
          }]
        };
      }
    }

    return { valid: true, data: ssn as any };
  }

  // =====================================================
  // Email Validation
  // =====================================================

  /**
   * Validates email address format
   */
  validateEmail(email: string): ValidationResult {
    if (!email || email.trim().length === 0) {
      return { valid: true }; // Email is optional
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!emailRegex.test(email)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'email',
          column: '',
          value: email,
          error: 'Invalid email format',
          code: 'invalid_format'
        }]
      };
    }

    if (email.length > 255) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'email',
          column: '',
          value: email,
          error: 'Email must be 255 characters or less',
          code: 'too_long'
        }]
      };
    }

    return { valid: true, data: email as any };
  }

  // =====================================================
  // State Code Validation
  // =====================================================

  /**
   * Validates US state code
   *
   * Accepts: 2-letter state codes (50 states + DC + 5 territories)
   */
  validateState(state: string): ValidationResult {
    if (!state || state.trim().length === 0) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'state',
          column: '',
          value: state,
          error: 'State code is required',
          code: 'required'
        }]
      };
    }

    const upperState = state.toUpperCase();

    if (!US_STATES.includes(upperState as any)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'state',
          column: '',
          value: state,
          error: 'Invalid state code. Must be a valid 2-letter US state code.',
          code: 'invalid_enum'
        }]
      };
    }

    return { valid: true, data: upperState as any };
  }

  // =====================================================
  // Zip Code Validation
  // =====================================================

  /**
   * Validates US zip code format
   *
   * Accepts: 12345 or 12345-6789
   */
  validateZipCode(zipCode: string): ValidationResult {
    if (!zipCode || zipCode.trim().length === 0) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'zipCode',
          column: '',
          value: zipCode,
          error: 'Zip code is required',
          code: 'required'
        }]
      };
    }

    const zipCodeRegex = /^\d{5}(-\d{4})?$/;

    if (!zipCodeRegex.test(zipCode)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'zipCode',
          column: '',
          value: zipCode,
          error: 'Invalid zip code format. Expected: 12345 or 12345-6789',
          code: 'invalid_format'
        }]
      };
    }

    return { valid: true, data: zipCode as any };
  }

  // =====================================================
  // Date Validation
  // =====================================================

  /**
   * Validates date format and constraints
   *
   * Accepts: Date object or ISO date string
   */
  validateDate(date: Date | string, fieldName: string = 'date'): ValidationResult {
    if (!date) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: fieldName,
          column: '',
          value: date,
          error: `${fieldName} is required`,
          code: 'required'
        }]
      };
    }

    let parsedDate: Date;

    if (typeof date === 'string') {
      parsedDate = new Date(date);
    } else if (date instanceof Date) {
      parsedDate = date;
    } else {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: fieldName,
          column: '',
          value: date,
          error: 'Invalid date type',
          code: 'invalid_type'
        }]
      };
    }

    // Check if date is valid
    if (isNaN(parsedDate.getTime())) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: fieldName,
          column: '',
          value: date,
          error: 'Invalid date format',
          code: 'invalid_format'
        }]
      };
    }

    // For formation date, ensure it's not in the future
    if (fieldName === 'entityFormationDate') {
      if (parsedDate > new Date()) {
        return {
          valid: false,
          errors: [{
            row: 0,
            field: fieldName,
            column: '',
            value: date,
            error: 'Formation date cannot be in the future',
            code: 'invalid_date'
          }]
        };
      }
    }

    return { valid: true, data: parsedDate as any };
  }

  // =====================================================
  // MCC Validation
  // =====================================================

  /**
   * Validates Merchant Category Code (MCC)
   *
   * Accepts: 4-digit numeric code
   */
  validateMcc(mcc: string): ValidationResult {
    if (!mcc || mcc.trim().length === 0) {
      return { valid: true }; // MCC is optional (conditionally required)
    }

    if (mcc.length !== 4) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'mcc',
          column: '',
          value: mcc,
          error: 'MCC must be exactly 4 digits',
          code: 'invalid_length'
        }]
      };
    }

    if (!/^\d{4}$/.test(mcc)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'mcc',
          column: '',
          value: mcc,
          error: 'MCC must be numeric',
          code: 'invalid_format'
        }]
      };
    }

    return { valid: true, data: mcc as any };
  }

  // =====================================================
  // NAICS Validation
  // =====================================================

  /**
   * Validates North American Industry Classification System (NAICS) code
   *
   * Accepts: 6-digit numeric code
   */
  validateNaics(naics: string): ValidationResult {
    if (!naics || naics.trim().length === 0) {
      return { valid: true }; // NAICS is optional (conditionally required)
    }

    if (naics.length !== 6) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'naics',
          column: '',
          value: naics,
          error: 'NAICS must be exactly 6 digits',
          code: 'invalid_length'
        }]
      };
    }

    if (!/^\d{6}$/.test(naics)) {
      return {
        valid: false,
        errors: [{
          row: 0,
          field: 'naics',
          column: '',
          value: naics,
          error: 'NAICS must be numeric',
          code: 'invalid_format'
        }]
      };
    }

    return { valid: true, data: naics as any };
  }

  // =====================================================
  // Full Row Validation
  // =====================================================

  /**
   * Validates a complete merchant upload row using Zod schema
   *
   * @param row - Raw merchant data from Excel
   * @param rowNumber - Row number for error reporting
   * @returns ValidationResult with data or errors
   */
  validateRow(row: MerchantUploadRowInput, rowNumber: number): ValidationResult {
    try {
      const validatedData = merchantUploadRowSchema.parse(row);
      return {
        valid: true,
        rowNumber,
        data: validatedData
      };
    } catch (error) {
      if (error instanceof z.ZodError) {
        const formattedErrors = this.formatZodErrors(error, rowNumber);
        return {
          valid: false,
          rowNumber,
          errors: formattedErrors
        };
      }

      // Unexpected error
      return {
        valid: false,
        rowNumber,
        errors: [{
          row: rowNumber,
          field: 'unknown',
          column: '',
          value: null,
          error: error instanceof Error ? error.message : 'Unknown validation error',
          code: 'unknown'
        }]
      };
    }
  }

  // =====================================================
  // Error Formatting
  // =====================================================

  /**
   * Formats Zod validation errors for user display
   */
  formatZodErrors(error: z.ZodError, rowNumber: number): FormattedValidationError[] {
    if (!error || !error.errors || !Array.isArray(error.errors)) {
      return [{
        row: rowNumber,
        field: 'unknown',
        column: '',
        value: null,
        error: 'Validation error occurred',
        code: 'unknown'
      }];
    }

    return error.errors.map(err => ({
      row: rowNumber,
      field: this.getFieldLabel(err.path.join('.')),
      column: this.getExcelColumn(err.path[0] as string),
      value: (err as any).input || null,
      error: err.message,
      code: err.code
    }));
  }

  /**
   * Maps field path to user-friendly label
   */
  private getFieldLabel(fieldPath: string): string {
    const labelMap: Record<string, string> = {
      'submissionDate': 'Submission Date',
      'tpppName': 'TPPP Name',
      'referralPartnerName': 'Referral Partner Name',
      'referralPartnerNumber': 'Referral Partner Number',
      'merchantId': 'Merchant ID',
      'companyId': 'Company ID',
      'hasMultipleIds': 'Has Multiple IDs',
      'legalEntityName': 'Legal Entity Name',
      'entityDba': 'Entity DBA',
      'address': 'Address',
      'city': 'City',
      'state': 'State',
      'zipCode': 'Zip Code',
      'entityFormationDate': 'Entity Formation Date',
      'tin': 'TIN',
      'phone': 'Phone',
      'email': 'Email',
      'mcc': 'MCC',
      'naics': 'NAICS',
      'businessDescription': 'Business Description',
      'accountUse': 'Account Use',
      'businessWebsite': 'Business Website',
      'cardDescriptor': 'Card Descriptor',
      'approvedCpPercentage': 'Approved CP %',
      'approvedCnpPercentage': 'Approved CNP %',
      'averageMonthlyVolume': 'Average Monthly Volume',
      'averageTicket': 'Average Ticket',
      'highTicket': 'High Ticket',
      'fulfillmentTimeframe': 'Fulfillment Timeframe',
      'fulfillmentVendor': 'Fulfillment Vendor',
      'depositsToBank': 'Deposits to Bank',
      'debitMonthlyLimit': 'Debit Monthly Limit',
      'creditMonthlyLimit': 'Credit Monthly Limit',
      'achHoldDays': 'ACH Hold Days',
      'reserveRequired': 'Reserve Required',
      'reserveContributionPercentage': 'Reserve Contribution %',
      'prefundAmount': 'Prefund Amount',
      'netSettlementEnabled': 'Net Settlement Enabled',
      'signerName': 'Signer Name',
      'signerSsn': 'Signer SSN',
      'beneficialOwner1Name': 'Beneficial Owner 1 Name',
      'beneficialOwner1Ssn': 'Beneficial Owner 1 SSN',
    };

    return labelMap[fieldPath] || fieldPath;
  }

  /**
   * Maps field name to Excel column letter
   *
   * Note: This mapping should match the Excel template structure
   * Column letters are approximate and should be verified against actual template
   */
  getExcelColumn(fieldName: string): string {
    const columnMap: Record<string, string> = {
      'submissionDate': 'A',
      'tpppName': 'B',
      'referralPartnerName': 'C',
      'referralPartnerNumber': 'D',
      'merchantId': 'E',
      'companyId': 'F',
      'hasMultipleIds': 'G',
      'legalEntityName': 'H',
      'entityDba': 'I',
      'address': 'J',
      'city': 'K',
      'state': 'L',
      'zipCode': 'M',
      'entityFormationDate': 'N',
      'tin': 'O',
      'phone': 'P',
      'email': 'Q',
      'mcc': 'R',
      'naics': 'S',
      'businessDescription': 'T',
      'accountUse': 'U',
      'businessWebsite': 'V',
      'cardDescriptor': 'W',
      // Add more mappings as needed (112 fields total)
    };

    return columnMap[fieldName] || '?';
  }
}

