/**
 * Sandbox API Testing Script
 * Tests MATCH Pro, ORC, and OFAC APIs with sandbox data
 *
 * Usage: npm run test:sandbox
 */

import axios from 'axios';
import chalk from 'chalk';

// Configuration
const BASE_URL = process.env.API_BASE_URL || 'http://localhost:3001';
const TEST_ICA = '1996'; // Sandbox ICA

interface TestResult {
  name: string;
  passed: boolean;
  duration: number;
  response?: any;
  error?: string;
}

const results: TestResult[] = [];

// Utility Functions
function logTest(name: string) {
  console.log(chalk.blue(`\nüß™ Testing: ${name}`));
}

function logSuccess(message: string) {
  console.log(chalk.green(`‚úì ${message}`));
}

function logError(message: string) {
  console.log(chalk.red(`‚úó ${message}`));
}

function logInfo(message: string) {
  console.log(chalk.gray(`  ${message}`));
}

async function runTest(
  name: string,
  testFn: () => Promise<any>
): Promise<void> {
  logTest(name);
  const startTime = Date.now();

  try {
    const response = await testFn();
    const duration = Date.now() - startTime;

    results.push({
      name,
      passed: true,
      duration,
      response
    });

    logSuccess(`Passed (${duration}ms)`);
    return response;
  } catch (error: any) {
    const duration = Date.now() - startTime;

    results.push({
      name,
      passed: false,
      duration,
      error: error.message
    });

    logError(`Failed (${duration}ms)`);
    logError(error.message);

    if (error.response?.data) {
      logInfo(`Response: ${JSON.stringify(error.response.data, null, 2)}`);
    }
  }
}

// =============================================================================
// MATCH Pro Tests
// =============================================================================

async function testMatchProCleanMerchant() {
  await runTest('MATCH Pro - Clean Merchant Inquiry', async () => {
    const response = await axios.post(`${BASE_URL}/api/match/inquiries`, {
      merchant: {
        name: 'CLEAN MERCHANT LLC',
        doingBusinessAsName: 'Clean Store',
        address: {
          line1: '123 Main Street',
          city: 'Springfield',
          state: 'IL',
          postalCode: '62701',
          country: 'USA'
        },
        phoneNumber: '2175551234',
        merchantCategoryCode: '5411',
        url: 'www.cleanmerchant.com'
      },
      principals: [
        {
          firstName: 'John',
          lastName: 'Clean',
          address: {
            line1: '123 Main Street',
            city: 'Springfield',
            state: 'IL',
            postalCode: '62701',
            country: 'USA'
          }
        }
      ],
      searchCriteria: {
        searchAll: true
      }
    });

    logInfo(`IRN: ${response.data.inquiryReferenceNumber}`);
    logInfo(`Matches: ${response.data.possibleMatches?.length || 0}`);

    return response.data;
  });
}

async function testMatchProFraudulentMerchant() {
  await runTest('MATCH Pro - Fraudulent Merchant Inquiry', async () => {
    const response = await axios.post(`${BASE_URL}/api/match/inquiries`, {
      merchant: {
        name: 'FRAUD MERCHANT INC',
        doingBusinessAsName: 'Bad Actor Store',
        address: {
          line1: '456 Risk Avenue',
          city: 'Miami',
          state: 'FL',
          postalCode: '33101',
