          nationalId: '123456789',
          address: {
            line1: '456 Risk Avenue',
            city: 'Miami',
            state: 'FL',
            postalCode: '33101',
            country: 'USA'
          }
        }
      ],
      searchCriteria: {
        searchAll: true
      }
    });

    logInfo(`IRN: ${response.data.inquiryReferenceNumber}`);
    logInfo(`Matches: ${response.data.possibleMatches?.length || 0}`);

    if (response.data.possibleMatches?.length > 0) {
      const match = response.data.possibleMatches[0];
      logInfo(`Match Type: ${match.matchIndicator}`);
      logInfo(`Reason Code: ${match.reasonCode}`);
