async function testMatchProValidationError() {
  await runTest('MATCH Pro - Validation Error', async () => {
    try {
      await axios.post(`${BASE_URL}/api/match/inquiries`, {
        merchant: {
          name: 'INCOMPLETE MERCHANT'
          // Missing required fields
        }
      });
      throw new Error('Should have thrown validation error');
    } catch (error: any) {
      if (error.response?.status === 400) {
        logInfo('Validation error caught correctly');
        return { validationPassed: true };
      }
      throw error;
    }
  });
}

