async function runAllTests() {
  console.log(chalk.cyan.bold('\n‚ïî‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïó'));
  console.log(chalk.cyan.bold('‚ïë     Sandbox API Testing Suite                  ‚ïë'));
  console.log(chalk.cyan.bold('‚ïö‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïù\n'));

  console.log(chalk.yellow(`Testing against: ${BASE_URL}\n`));

  // MATCH Pro Tests
  console.log(chalk.magenta.bold('\n‚îÅ‚îÅ‚îÅ MATCH Pro API Tests ‚îÅ‚îÅ‚îÅ'));
  await testMatchProCleanMerchant();
  await testMatchProFraudulentMerchant();
  await testMatchProAddTerminated();
  await testMatchProValidationError();

  // ORC Tests
  console.log(chalk.magenta.bold('\n‚îÅ‚îÅ‚îÅ ORC API Tests ‚îÅ‚îÅ‚îÅ'));
  await testORCCyberHealth();
  await testORCSanctionsScreening();
  await testORCBusinessIdentity();
  await testORCCombined();
  await testORCRetrieveResults();

  // OFAC Tests
  console.log(chalk.magenta.bold('\n‚îÅ‚îÅ‚îÅ OFAC Screening Tests ‚îÅ‚îÅ‚îÅ'));
  await testOFACCleanEntity();
  await testOFACSanctionedEntity();

  // Summary
  console.log(chalk.cyan.bold('\n‚ïî‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïó'));
  console.log(chalk.cyan.bold('‚ïë     Test Results Summary                       ‚ïë'));
  console.log(chalk.cyan.bold('‚ïö‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïù\n'));

  const passed = results.filter(r => r.passed).length;
  const failed = results.filter(r => !r.passed).length;
  const total = results.length;
  const avgDuration = Math.round(
    results.reduce((sum, r) => sum + r.duration, 0) / total
  );

  console.log(chalk.green(`‚úì Passed: ${passed}/${total}`));
  console.log(chalk.red(`‚úó Failed: ${failed}/${total}`));
  console.log(chalk.gray(`‚è±  Average Duration: ${avgDuration}ms\n`));

  if (failed > 0) {
    console.log(chalk.red.bold('Failed Tests:'));
    results
      .filter(r => !r.passed)
      .forEach(r => {
        console.log(chalk.red(`  - ${r.name}`));
        console.log(chalk.gray(`    ${r.error}`));
      });
  }

  console.log(chalk.cyan('\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n'));

  process.exit(failed > 0 ? 1 : 0);
}

// Run tests
runAllTests().catch(error => {
  console.error(chalk.red.bold('\n‚ùå Test suite failed:'));
  console.error(error);
  process.exit(1);
});

