'use client'

import Link from 'next/link'
import { usePathname } from 'next/navigation'
import { cn } from '@/lib/utils'
import { Button } from '@/components/ui/button'
import { useUIStore, useUISelectors, useResponsiveUI } from '@/lib/stores/ui-store'
import type { AppUser } from '@/lib/auth'
import { useClerk } from '@clerk/nextjs'
import {
  Home,
  Search,
  UserPlus,
  History,
  Shield,
  FileText,
  Settings,
  Menu,
  X,
  ChevronLeft,
  ChevronRight,
  LogOut,
  AlertTriangle,
  Building,
  Ban,
  Eye,
  BarChart3,
} from 'lucide-react'
import { useEffect, useTransition } from 'react'

interface NavItem {
  href: string
  label: string
  icon: React.ComponentType<{ className?: string }>
  description?: string
  badge?: string
  requiredRole?: string[]
  requiredUserType?: string[]
  adminOnly?: boolean
}

const navigation: NavItem[] = [
  {
    href: '/dashboard',
    label: 'Dashboard',
    icon: Home,
    description: 'Overview and quick actions',
  },
  {
    href: '/match/inquiry',
    label: 'MATCH Inquiry',
    icon: Search,
    description: 'Search merchant database',
    badge: 'MATCH',
    requiredRole: ['admin', 'operator', 'viewer'],
    requiredUserType: ['bank', 'iso', 'agent'],
  },
  {
    href: '/match/add-merchant',
    label: 'Add Merchant',
    icon: UserPlus,
    description: 'Register new merchant',
    badge: 'MATCH',
    requiredRole: ['admin', 'operator'],
    requiredUserType: ['bank', 'iso'],
  },
  {
    href: '/match/history',
    label: 'Inquiry History',
    icon: History,
    description: 'View past inquiries',
    badge: 'MATCH',
    requiredRole: ['admin', 'operator', 'viewer'],
    requiredUserType: ['bank', 'iso', 'agent'],
  },
  {
    href: '/ofac/screening',
    label: 'OFAC Screening',
    icon: Ban,
    description: 'Screen against sanctions lists',
    badge: 'OFAC',
    requiredRole: ['admin', 'operator'],
    requiredUserType: ['bank', 'iso', 'agent'],
  },
  {
    href: '/ofac/monitoring',
    label: 'Monitoring Queue',
    icon: Eye,
    description: 'Manage monitoring tasks',
    badge: 'OFAC',
    requiredRole: ['admin', 'operator', 'viewer'],
    requiredUserType: ['bank', 'iso', 'agent'],
  },
  {
    href: '/ofac/reports',
    label: 'OFAC Reports',
    icon: BarChart3,
    description: 'Generate compliance reports',
    badge: 'OFAC',
    requiredRole: ['admin', 'operator', 'viewer'],
    requiredUserType: ['bank', 'iso'],
  },
  {
    href: '/orc/new-check',
    label: 'New Risk Check',
    icon: Shield,
    description: 'Perform ORC analysis',
    badge: 'ORC',
    requiredRole: ['admin', 'operator'],
    requiredUserType: ['bank', 'iso', 'agent'],
  },
  {
    href: '/orc/results',
    label: 'Risk Results',
    icon: AlertTriangle,
    description: 'View ORC results',
    badge: 'ORC',
    requiredRole: ['admin', 'operator', 'viewer'],
    requiredUserType: ['bank', 'iso', 'agent'],
  },
  {
    href: '/reports',
    label: 'Reports',
    icon: FileText,
    description: 'Generate reports',
    requiredRole: ['admin', 'operator', 'viewer'],
    requiredUserType: ['bank', 'iso'],
  },
  {
    href: '/admin/registrations',
    label: 'Manage Registrations',
    icon: UserPlus,
    description: 'Review pending registrations',
    adminOnly: true,
    requiredUserType: ['bank'],
  },
  {
    href: '/admin/ica-management',
    label: 'ICA Management',
    icon: Building,
    description: 'Manage ICA assignments',
    adminOnly: true,
    requiredUserType: ['bank'],
  },
  {
    href: '/settings',
    label: 'Settings',
    icon: Settings,
    description: 'Account preferences',
  },
]

interface DashboardNavClientProps {
  user: AppUser
}

export function DashboardNavClient({ user }: DashboardNavClientProps) {
  const pathname = usePathname()
  const { closeOnMobile, checkViewport, isMobile } = useResponsiveUI()
  const [isPending, startTransition] = useTransition()
  const { signOut } = useClerk()

  const {
    sidebarWidth,
    isSidebarCollapsed,
    isSidebarHidden,
    isMobileSidebarOpen,
  } = useUISelectors()

  const {
    toggleSidebar,
    toggleMobileSidebar,
    setMobileSidebarOpen,
  } = useUIStore()

  // Handle viewport changes
  useEffect(() => {
    checkViewport()

    const handleResize = () => {
      checkViewport()
    }

    window.addEventListener('resize', handleResize)
    return () => window.removeEventListener('resize', handleResize)
  }, [checkViewport, isMobile])

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Toggle sidebar with Ctrl/Cmd + B
      if ((e.ctrlKey || e.metaKey) && e.key === 'b') {
        e.preventDefault()
        if (isMobile()) {
          toggleMobileSidebar()
        } else {
          toggleSidebar()
        }
      }

      // Close mobile sidebar with Escape
      if (e.key === 'Escape' && isMobileSidebarOpen) {
        setMobileSidebarOpen(false)
      }
    }

    document.addEventListener('keydown', handleKeyDown)
    return () => document.removeEventListener('keydown', handleKeyDown)
  }, [isMobileSidebarOpen, setMobileSidebarOpen, toggleMobileSidebar, toggleSidebar, isMobile])

  const handleLogout = () => {
    startTransition(async () => {
      await signOut({ redirectUrl: '/sign-in' })
    })
  }

  const handleNavClick = () => {
    closeOnMobile()
  }

  const isActive = (href: string) => {
    if (href === '/dashboard') {
      return pathname === '/dashboard'
    }
    return pathname.startsWith(href)
  }

  const hasAccess = (item: NavItem) => {
    if (!user) return false

    // Check admin only items
    if (item.adminOnly && user.role !== 'admin') {
      return false
    }

    // Check required user type
    if (item.requiredUserType && user.userType && !item.requiredUserType.includes(user.userType)) {
      return false
    }

    // Check required role
    if (item.requiredRole && user.role && !item.requiredRole.includes(user.role)) {
      return false
    }

    return true
  }

  const getAccessLevelIndicator = (item: NavItem) => {
    if (!user) return null

    // Show restriction indicators for items with limited access
    if (item.requiredUserType && item.requiredUserType.length < 3) {
      if (item.requiredUserType.includes('bank') && !item.requiredUserType.includes('agent')) {
        return 'Admin Only'
      }
      if (item.requiredUserType.includes('agent') && !item.requiredUserType.includes('bank')) {
        return 'Agent Access'
      }
    }

    if (item.requiredRole && item.requiredRole.length < 3) {
      if (item.requiredRole.includes('admin') && !item.requiredRole.includes('viewer')) {
        return 'Admin/Operator'
      }
    }

    return null
  }

  // Don't render if no user data yet
  if (!user) {
    return null
  }

  const sidebarContent = (
    <div className="flex h-full flex-col">
      {/* Header */}
      <div className={cn(
        "flex items-center gap-3 border-b px-4 py-4",
        isSidebarCollapsed && "justify-center px-2"
      )}>
        <div className="flex h-8 w-8 items-center justify-center rounded-lg bg-blue-600 text-white font-bold text-sm">
          M
        </div>
        {!isSidebarCollapsed && (
          <div className="flex-1 min-w-0">
            <h1 className="text-lg font-semibold text-gray-900 truncate">
              MATCH Pro, OFAC & ORC
            </h1>
          </div>
        )}
        {/* Desktop toggle button */}
        {!isMobile() && (
          <Button
            variant="ghost"
            size="sm"
            onClick={toggleSidebar}
            className="hidden lg:flex shrink-0"
            aria-label={isSidebarCollapsed ? 'Expand sidebar' : 'Collapse sidebar'}
          >
            {isSidebarCollapsed ? (
              <ChevronRight className="h-4 w-4" />
            ) : (
              <ChevronLeft className="h-4 w-4" />
            )}
          </Button>
        )}
      </div>

      {/* Navigation */}
      <nav className="flex-1 space-y-1 p-2 overflow-y-auto">
        {navigation.filter(hasAccess).map((item) => {
          const Icon = item.icon
          const active = isActive(item.href)
          const accessIndicator = getAccessLevelIndicator(item)

          return (
            <Link
              key={item.href}
              href={item.href}
              onClick={handleNavClick}
              className={cn(
                "group flex items-center gap-3 rounded-lg px-3 py-2.5 text-sm font-medium transition-all duration-200",
                "hover:bg-gray-100 focus:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2",
                active && "bg-blue-50 text-blue-700 border-r-2 border-blue-600",
                !active && "text-gray-700 hover:text-gray-900",
                isSidebarCollapsed && "justify-center px-2"
              )}
              title={isSidebarCollapsed ? item.label : undefined}
            >
              <Icon className={cn(
                "h-5 w-5 shrink-0 transition-colors",
                active ? "text-blue-600" : "text-gray-500 group-hover:text-gray-700"
              )} />

              {!isSidebarCollapsed && (
                <>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2">
                      <span className="truncate">{item.label}</span>
                      {item.badge && (
                        <span className={cn(
                          "inline-flex items-center rounded-full px-2 py-0.5 text-xs font-medium",
                          item.badge === 'MATCH' && "bg-green-100 text-green-800",
                          item.badge === 'OFAC' && "bg-red-100 text-red-800",
                          item.badge === 'ORC' && "bg-orange-100 text-orange-800"
                        )}>
                          {item.badge}
                        </span>
                      )}
                      {accessIndicator && (
                        <span className="inline-flex items-center rounded-full px-2 py-0.5 text-xs font-medium bg-purple-100 text-purple-800">
                          {accessIndicator}
                        </span>
                      )}
                    </div>
                    {item.description && (
                      <p className="text-xs text-gray-500 mt-0.5 truncate">
                        {item.description}
                      </p>
                    )}
                  </div>
                </>
              )}
            </Link>
          )
        })}
      </nav>

      {/* User section */}
      <div className={cn(
        "border-t p-4 space-y-2",
        isSidebarCollapsed && "px-2"
      )}>
        {!isSidebarCollapsed && (
          <div className="text-sm space-y-2">
            <div>
              <p className="font-medium text-gray-900 truncate">{user.username}</p>
              <div className="flex items-center gap-2 mt-1">
                <span className="inline-flex items-center rounded-full px-2 py-1 text-xs font-medium bg-blue-100 text-blue-800">
                  {user.role}
                </span>
                {user.userType && (
                  <span className={cn(
                    "inline-flex items-center rounded-full px-2 py-1 text-xs font-medium",
                    user.userType === 'bank' && "bg-purple-100 text-purple-800",
                    user.userType === 'iso' && "bg-green-100 text-green-800",
                    user.userType === 'agent' && "bg-orange-100 text-orange-800"
                  )}>
                    {user.userType.toUpperCase()}
                  </span>
                )}
              </div>
            </div>
            {user.icaNumber && (
              <div className="text-xs">
                <p className="text-gray-500">ICA: <span className="font-mono text-gray-700">{user.icaNumber}</span></p>
              </div>
            )}
            {user.userType === 'agent' && user.merchantCount !== undefined && (
              <div className="text-xs">
                <p className="text-gray-500">Merchant Access: <span className="font-medium text-gray-700">{user.merchantCount} merchants</span></p>
              </div>
            )}
          </div>
        )}

        <Button
          variant="ghost"
          size={isSidebarCollapsed ? "sm" : "default"}
          onClick={handleLogout}
          disabled={isPending}
          className={cn(
            "w-full justify-start gap-2 text-gray-700 hover:text-gray-900",
            isSidebarCollapsed && "justify-center px-2"
          )}
          title={isSidebarCollapsed ? 'Logout' : undefined}
        >
          <LogOut className="h-4 w-4 shrink-0" />
          {!isSidebarCollapsed && (
            <span>{isPending ? 'Signing out...' : 'Logout'}</span>
          )}
        </Button>
      </div>
    </div>
  )

  // Mobile sidebar with overlay
  if (isMobile()) {
    return (
      <>
        {/* Mobile menu button */}
        <Button
          variant="ghost"
          size="sm"
          onClick={toggleMobileSidebar}
          className="lg:hidden"
          aria-label="Toggle navigation menu"
        >
          <Menu className="h-5 w-5" />
        </Button>

        {/* Mobile sidebar overlay */}
        {isMobileSidebarOpen && (
          <div className="fixed inset-0 z-50 lg:hidden">
            {/* Backdrop */}
            <div
              className="fixed inset-0 bg-black/50 transition-opacity"
              onClick={() => setMobileSidebarOpen(false)}
            />

            {/* Sidebar */}
            <div className="fixed inset-y-0 left-0 w-72 bg-white shadow-xl transform transition-transform">
              {/* Mobile close button */}
              <div className="absolute right-4 top-4">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setMobileSidebarOpen(false)}
                  aria-label="Close navigation menu"
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>

              {sidebarContent}
            </div>
          </div>
        )}
      </>
    )
  }

  // Desktop sidebar
  return (
    <div
      className={cn(
        "hidden lg:flex lg:flex-col lg:fixed lg:inset-y-0 lg:z-40 bg-white border-r border-gray-200 transition-all duration-300",
        isSidebarHidden && "lg:hidden"
      )}
      style={{ width: isSidebarHidden ? 0 : sidebarWidth }}
    >
      {sidebarContent}
    </div>
  )
}

// Export for backward compatibility
export const DashboardNav = DashboardNavClient

