'use client'

import { useState, useEffect, useActionState } from 'react'
import { useForm } from 'react-hook-form'
import { zodResolver } from '@hookform/resolvers/zod'
import { Button } from '@/components/ui/button'
import { Form } from '@/components/ui/form'
import { ArrowLeft, ArrowRight, Save, Send } from 'lucide-react'

// Flattened components
import { ProgressStepper } from './risk-check-progress-stepper'
import { BusinessInformationStep } from './risk-check-business-info-step'
import { CheckOptionsStep } from './risk-check-options-step'
import { ReviewSubmitStep } from './risk-check-review-step'
import { submitRiskCheck, saveDraft } from '@/app/actions/orc'
import { toast } from 'sonner'
import { useCsrfToken } from '@/hooks/use-csrf-token'
import { ErrorBoundary } from '@/components/error-boundary'
import { FormErrorFallback } from '@/components/error-fallback'
import {
  fullSchema,
  formDefaults,
  steps,
  type FormData,
} from './risk-check-form-types'


function RiskCheckFormContent() {
  const form = useForm<FormData>({
    resolver: zodResolver(fullSchema),
    defaultValues: formDefaults,
  })

  // Get CSRF token for form security
  const { token: csrfToken, loading: csrfLoading } = useCsrfToken()

  // Inline form logic (previously useRiskCheckForm hook)
  const [currentStep, setCurrentStep] = useState(1)
  const [draftSaved, setDraftSaved] = useState(false)

  const [submitState, submitAction, isPending] = useActionState(submitRiskCheck, {
    success: false,
    data: null,
    error: null
  })

  const [draftState, draftAction, isDraftPending] = useActionState(saveDraft, {
    success: false,
    data: null,
    error: null
  })

  // Handle submit state changes
  useEffect(() => {
    if (submitState.success && submitState.data) {
      toast.success('Risk check submitted successfully!', {
        description: `IRN: ${submitState.data.irn}`,
      })
      // Redirect to results page with IRN
    } else if (submitState.error) {
      toast.error('Failed to submit risk check', {
        description: submitState.error,
      })
    }
  }, [submitState])

  // Handle draft state changes
  useEffect(() => {
    if (draftState.success && draftState.data) {
      setDraftSaved(true)
      toast.success('Draft saved successfully!')
      setTimeout(() => setDraftSaved(false), 3000)
    } else if (draftState.error) {
      toast.error('Failed to save draft', {
        description: draftState.error,
      })
    }
  }, [draftState])

  const onSubmit = (data: FormData) => {
    // Prevent submission if CSRF token is not available
    if (!csrfToken) {
      toast.error('Security token missing. Please refresh the page.')
      return
    }

    // Create a FormData object and submit via submitAction
    const formData = new FormData()

    // Add CSRF token first (security best practice)
    formData.append('csrfToken', csrfToken)

    formData.append('companyName', data.companyName)
    formData.append('businessType', data.businessType)
    formData.append('industry', data.industry)
    formData.append('address.street', data.address.street)
    formData.append('address.city', data.address.city)
    formData.append('address.state', data.address.state)
    formData.append('address.postalCode', data.address.postalCode)
    formData.append('address.country', data.address.country)
    formData.append('cyberRisk', data.cyberRisk.toString())
    formData.append('sanctionsCheck', data.sanctionsCheck.toString())
    formData.append('businessVerification', data.businessVerification.toString())
    if (data.additionalNotes) {
      formData.append('additionalInfo', data.additionalNotes)
    }

    submitAction(formData)
  }

  const saveDraftHandler = () => {
    // Prevent submission if CSRF token is not available
    if (!csrfToken) {
      toast.error('Security token missing. Please refresh the page.')
      return
    }

    const data = form.getValues()

    // Create a FormData object and submit via draftAction
    const formData = new FormData()

    // Add CSRF token first (security best practice)
    formData.append('csrfToken', csrfToken)

    formData.append('companyName', data.companyName)
    formData.append('businessType', data.businessType)
    formData.append('industry', data.industry)
    // Add other relevant fields for draft saving

    draftAction(formData)
  }

  const nextStep = async () => {
    const fieldsToValidate = getFieldsForStep(currentStep)
    const isValid = await form.trigger(fieldsToValidate)

    if (isValid && currentStep < steps.length) {
      setCurrentStep(currentStep + 1)
    }
  }

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1)
    }
  }

  const getFieldsForStep = (step: number): (keyof FormData)[] => {
    switch (step) {
      case 1:
        return ['companyName', 'businessType', 'industry', 'address']
      case 2:
        return ['cyberRisk', 'sanctionsCheck', 'businessVerification']
      default:
        return []
    }
  }

  return (
    <div className="space-y-6">
      {/* Progress Indicator */}
      <ProgressStepper currentStep={currentStep} />

      <Form {...form}>
        <form
          onSubmit={form.handleSubmit(onSubmit)}
          action={submitAction}
          className="space-y-6"
        >
          {/* Step 1: Business Information */}
          {currentStep === 1 && (
            <BusinessInformationStep control={form.control} />
          )}

          {/* Step 2: Check Options */}
          {currentStep === 2 && (
            <CheckOptionsStep control={form.control} />
          )}

          {/* Step 3: Review & Submit */}
          {currentStep === 3 && (
            <ReviewSubmitStep form={form} />
          )}

          {/* Navigation Buttons */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {currentStep > 1 && (
                <Button type="button" variant="outline" onClick={prevStep}>
                  <ArrowLeft className="h-4 w-4 mr-2" />
                  Previous
                </Button>
              )}
            </div>

            <div className="flex items-center space-x-2">
              <Button
                type="button"
                variant="outline"
                onClick={saveDraftHandler}
                disabled={isDraftPending || csrfLoading || !csrfToken}
                className="flex items-center space-x-2"
              >
                <Save className="h-4 w-4" />
                <span>{draftSaved ? 'Saved!' : isDraftPending ? 'Saving...' : csrfLoading ? 'Loading...' : 'Save Draft'}</span>
              </Button>

              {currentStep < steps.length ? (
                <Button type="button" onClick={nextStep}>
                  Next
                  <ArrowRight className="h-4 w-4 ml-2" />
                </Button>
              ) : (
                <Button type="submit" disabled={isPending || csrfLoading || !csrfToken}>
                  <Send className="h-4 w-4 mr-2" />
                  {isPending ? 'Submitting...' : csrfLoading ? 'Loading...' : 'Submit Check'}
                </Button>
              )}
            </div>
          </div>
        </form>
      </Form>
    </div>
  )
}

export function RiskCheckForm() {
  return (
    <ErrorBoundary
      componentName="RiskCheckForm"
      fallback={(error, reset) => (
        <FormErrorFallback error={error} reset={reset} formName="Risk Check Form" />
      )}
    >
      <RiskCheckFormContent />
    </ErrorBoundary>
  )
}

