import { z } from 'zod'

// Base schema with optional fields
export const businessInfoSchema = z.object({
  companyName: z.string().min(2, 'Company name must be at least 2 characters'),
  registrationNumber: z.string().optional(),
  businessType: z.enum(['corporation', 'llc', 'partnership', 'sole_proprietorship', 'other']),
  industry: z.string().optional(),
  description: z.string().optional(),
  website: z.string().optional(),
  address: z.object({
    street: z.string().optional(),
    city: z.string().optional(),
    state: z.string().optional(),
    postalCode: z.string().optional(),
    country: z.string().min(1, 'Country is required'),
  }),
})

export const checkOptionsSchema = z.object({
  cyberRisk: z.boolean().default(false),
  sanctionsCheck: z.boolean().default(false),
  businessVerification: z.boolean().default(false),
  additionalNotes: z.string().optional(),
})

// Full schema with conditional validation based on selected options
export const fullSchema = businessInfoSchema.merge(checkOptionsSchema).superRefine((data, ctx) => {
  // Cyber Health validation: requires website
  if (data.cyberRisk && !data.website) {
    ctx.addIssue({
      code: z.ZodIssueCode.custom,
      message: 'Website is required for Cyber Health assessment',
      path: ['website'],
    })
  }

  // Sanctions Screening or Business Identity: require address fields
  if (data.sanctionsCheck || data.businessVerification) {
    if (!data.address.street) {
      ctx.addIssue({
        code: z.ZodIssueCode.custom,
        message: 'Street address is required',
        path: ['address', 'street'],
      })
    }
    if (!data.address.city) {
      ctx.addIssue({
        code: z.ZodIssueCode.custom,
        message: 'City is required',
        path: ['address', 'city'],
      })
    }
    if (!data.address.state) {
      ctx.addIssue({
        code: z.ZodIssueCode.custom,
        message: 'State is required',
        path: ['address', 'state'],
      })
    }
