    33‚Üí}
    34‚Üí
    35‚Üí// Helper function to get auth session
    36‚Üíasync function getAuthSession() {
    37‚Üí  return await auth()
    38‚Üí}
    39‚Üí
    40‚Üí// Timeout configuration based on operation type
    41‚Üíconst TIMEOUT_CONFIG = {
    42‚Üí  RISK_CHECK: 30000,   // 30s - Risk check operations
    43‚Üí  DRAFT: 10000,        // 10s - Quick draft saves
    44‚Üí  POLLING: 10000,      // 10s - Status polling
    45‚Üí  EXPORT: 60000,       // 60s - Report generation/export
    46‚Üí  BULK: 60000,         // 60s - Bulk processing
    47‚Üí}
    48‚Üí
    49‚Üí// Helper function to determine timeout based on endpoint
    50‚Üífunction getTimeoutForEndpoint(endpoint: string): number {
    51‚Üí  if (endpoint.includes('/check')) return TIMEOUT_CONFIG.RISK_CHECK
    52‚Üí  if (endpoint.includes('/draft')) return TIMEOUT_CONFIG.DRAFT
    53‚Üí  if (endpoint.includes('/status')) return TIMEOUT_CONFIG.POLLING
    54‚Üí  if (endpoint.includes('/export')) return TIMEOUT_CONFIG.EXPORT
    55‚Üí  if (endpoint.includes('/bulk')) return TIMEOUT_CONFIG.BULK
    56‚Üí  return 30000 // Default 30s
    57‚Üí}
    58‚Üí
    59‚Üí// Helper function to make API calls with secure JWT tokens
    60‚Üíasync function makeAPICall<T>(
    61‚Üí  endpoint: string,
    62‚Üí  options: RequestInit = {}
    63‚Üí): Promise<{ success: boolean; data?: T; error?: string }> {
    64‚Üí  const timeout = getTimeoutForEndpoint(endpoint)
    65‚Üí  const controller = new AbortController()
    66‚Üí  const timeoutId = setTimeout(() => controller.abort(), timeout)
    67‚Üí
    68‚Üí  try {
    69‚Üí    const { userId, getToken } = await getAuthSession()
    70‚Üí    if (!userId) {
    71‚Üí      clearTimeout(timeoutId)
    72‚Üí      return { success: false, error: 'Authentication required' }
    73‚Üí    }
    74‚Üí
    75‚Üí    // Get the secure JWT access token from Clerk
    76‚Üí    const accessToken = await getToken()
    77‚Üí    if (!accessToken) {
    78‚Üí      apiLogger.error('No access token from Clerk')
    79‚Üí      clearTimeout(timeoutId)
    80‚Üí      return { success: false, error: 'Invalid authentication token' }
    81‚Üí    }
    82‚Üí
    83‚Üí    // Get API base URL from environment variables with proper fallback
    84‚Üí    const getApiBaseUrl = (): string => {
    85‚Üí      if (process.env['API_BASE_URL']) {
    86‚Üí        return process.env['API_BASE_URL']
    87‚Üí      }
    88‚Üí
    89‚Üí      // Development fallback - only in development environment
    90‚Üí      if (process.env.NODE_ENV === 'development') {
    91‚Üí        return 'http://localhost:3001'
    92‚Üí      }
    93‚Üí
    94‚Üí      // Production should always have proper environment variables set
    95‚Üí      throw new Error('API_BASE_URL must be configured in production')
    96‚Üí    }
    97‚Üí
    98‚Üí    const baseURL = getApiBaseUrl()
    99‚Üí    const response = await fetch(`${baseURL}${endpoint}`, {
   100‚Üí      headers: {
   101‚Üí        'Content-Type': 'application/json',
   102‚Üí        'Authorization': `Bearer ${accessToken}`, // Use secure JWT token
   103‚Üí        ...options.headers,
   104‚Üí      },
   105‚Üí      cache: 'no-store',
   106‚Üí      signal: controller.signal,
   107‚Üí      ...options,
   108‚Üí    })
   109‚Üí
   110‚Üí    clearTimeout(timeoutId)
   111‚Üí
   112‚Üí    if (!response.ok) {
   113‚Üí      const errorData = await response.text()
   114‚Üí      return {
   115‚Üí        success: false,
   116‚Üí        error: `API Error: ${response.status} - ${errorData}`,
   117‚Üí      }
   118‚Üí    }
   119‚Üí
   120‚Üí    const data = await response.json()
   121‚Üí    return { success: true, data }
   122‚Üí  } catch (error) {
   123‚Üí    clearTimeout(timeoutId)
   124‚Üí
   125‚Üí    // Handle timeout specifically
   126‚Üí    if (error instanceof Error && error.name === 'AbortError') {
   127‚Üí      apiLogger.error('API call timeout', { endpoint, timeout })
   128‚Üí      return {
   129‚Üí        success: false,
   130‚Üí        error: `Request timeout: The ORC operation took longer than expected (${timeout / 1000}s). Please try again.`,
   131‚Üí      }
   132‚Üí    }
   133‚Üí
   134‚Üí    apiLogger.error('API call failed', error)
   135‚Üí    return {
   136‚Üí      success: false,
   137‚Üí      error: error instanceof Error ? error.message : 'Unknown error occurred',
   138‚Üí    }
   139‚Üí  }
   140‚Üí}
