// ORC bulk processing action
export async function submitBulkRiskChecks(
  prevState: any,
  formData: FormData
): Promise<{ success: boolean; data?: { batchId: string; count: number } | null; error?: string | null }> {
  try {
    // CSRF Protection: Verify token before processing
    const csrfValidation = await validateCsrfFromFormData(formData)
    if (!csrfValidation.success) {
      apiLogger.error('CSRF validation failed for ORC bulk processing')
      return {
        success: false,
        data: null,
        error: csrfValidation.error || 'Security validation failed',
      }
    }

    // Rate limiting check
    const session = await getAuthSession()
    if (!session?.user?.id) {
      return { success: false, data: null, error: 'Authentication required' }
    }

    const userId = session.user.id
    if (!RateLimiter.checkLimit(`orc_bulk_${userId}`, 2, 3600000)) { // 2 requests per hour
      return {
        success: false,
        data: null,
        error: 'Too many bulk processing requests. Please wait before trying again.'
      }
    }

    // Extract and validate bulk data
    const companiesData = formData.get('companies')
    if (!companiesData) {
      return { success: false, data: null, error: 'Companies data is required' }
    }

    let companies: any[]
    try {
      companies = JSON.parse(companiesData as string)
    } catch {
      return { success: false, data: null, error: 'Invalid companies data format' }
    }

    if (!Array.isArray(companies) || companies.length === 0) {
      return { success: false, data: null, error: 'At least one company is required' }
    }

    if (companies.length > 100) {
      return { success: false, data: null, error: 'Maximum 100 companies allowed per batch' }
    }

    // Validate each company data
    const validatedCompanies = []
    for (let i = 0; i < companies.length; i++) {
      try {
        const validatedCompany = orcRiskCheckSchema.parse(companies[i])
        validatedCompanies.push(validatedCompany)
      } catch (error) {
        if (error instanceof ZodError) {
          const errorMessages = error.issues.map(err => `${err.path.join('.')}: ${err.message}`).join(', ')
          return {
            success: false,
            data: null,
            error: `Validation error for company ${i + 1}: ${errorMessages}`,
          }
        }
        return {
          success: false,
          data: null,
          error: `Invalid data for company ${i + 1}`,
        }
      }
    }

    businessLogger.info('Submitting bulk ORC risk checks', {
      companyCount: validatedCompanies.length
    })

    // Make API call to submit bulk checks
    const result = await makeAPICall<{ batchId: string; count: number }>('/api/orc/bulk', {
      method: 'POST',
      body: JSON.stringify({
        companies: validatedCompanies,
        timestamp: new Date().toISOString(),
      }),
    })

    if (!result.success) {
      return { success: false, data: null, error: result.error || null }
    }

    // Revalidate relevant caches
    revalidateTag('orc-checks')
    revalidateTag('orc-data')

    return { success: true, data: result.data || null, error: null }
  } catch (error) {
    apiLogger.error('ORC bulk processing failed', error)

    return {
      success: false,
      data: null,
      error: 'Failed to submit bulk risk checks. Please try again.',
    }
  }
}

