'use server'

import { revalidateTag } from 'next/cache'
import { auth } from '@clerk/nextjs/server'
import {
  orcRiskCheckSchema,
  orcDraftSchema,
  orcPollingSchema,
  orcExportSchema,
  type ORCRiskCheckData,
  type ORCDraftData,
  type ORCPollingData,
  type ORCExportData,
  RateLimiter
} from '@/lib/schemas'
import { apiLogger, businessLogger, validationLogger } from '@/lib/logger'
import { validateCsrfFromFormData } from '@/app/_lib/csrf'
import { ZodError } from 'zod'

// Types from validators (keeping for backwards compatibility)

interface RiskCheckResult {
  irn: string
  status: 'pending' | 'completed' | 'failed'
  riskScore: number
  riskLevel: 'low' | 'medium' | 'high'
  checks: {
    cyber: { status: string; score: number }
    sanctions: { status: string; matches: number }
    business: { status: string; verified: boolean }
  }
  createdAt: string
}

// Helper function to get auth session
async function getAuthSession() {
  return await auth()
}

// Timeout configuration based on operation type
const TIMEOUT_CONFIG = {
  RISK_CHECK: 30000,   // 30s - Risk check operations
  DRAFT: 10000,        // 10s - Quick draft saves
  POLLING: 10000,      // 10s - Status polling
  EXPORT: 60000,       // 60s - Report generation/export
  BULK: 60000,         // 60s - Bulk processing
}

// Helper function to determine timeout based on endpoint
function getTimeoutForEndpoint(endpoint: string): number {
  if (endpoint.includes('/check')) return TIMEOUT_CONFIG.RISK_CHECK
  if (endpoint.includes('/draft')) return TIMEOUT_CONFIG.DRAFT
  if (endpoint.includes('/status')) return TIMEOUT_CONFIG.POLLING
  if (endpoint.includes('/export')) return TIMEOUT_CONFIG.EXPORT
  if (endpoint.includes('/bulk')) return TIMEOUT_CONFIG.BULK
  return 30000 // Default 30s
}

// Helper function to make API calls with secure JWT tokens
async function makeAPICall<T>(
  endpoint: string,
  options: RequestInit = {}
): Promise<{ success: boolean; data?: T; error?: string }> {
  const timeout = getTimeoutForEndpoint(endpoint)
  const controller = new AbortController()
  const timeoutId = setTimeout(() => controller.abort(), timeout)

  try {
    const { userId, getToken } = await getAuthSession()
    if (!userId) {
      clearTimeout(timeoutId)
      return { success: false, error: 'Authentication required' }
    }

    // Get the secure JWT access token from Clerk
    const accessToken = await getToken()
    if (!accessToken) {
      apiLogger.error('No access token from Clerk')
      clearTimeout(timeoutId)
      return { success: false, error: 'Invalid authentication token' }
    }

    // Get API base URL from environment variables with proper fallback
    const getApiBaseUrl = (): string => {
      if (process.env['API_BASE_URL']) {
        return process.env['API_BASE_URL']
      }

      // Development fallback - only in development environment
      if (process.env.NODE_ENV === 'development') {
        return 'http://localhost:3001'
      }

      // Production should always have proper environment variables set
      throw new Error('API_BASE_URL must be configured in production')
    }

    const baseURL = getApiBaseUrl()
    const response = await fetch(`${baseURL}${endpoint}`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${accessToken}`, // Use secure JWT token
        ...options.headers,
      },
      cache: 'no-store',
      signal: controller.signal,
      ...options,
    })

    clearTimeout(timeoutId)

    if (!response.ok) {
      const errorData = await response.text()
      return {
        success: false,
        error: `API Error: ${response.status} - ${errorData}`,
      }
    }

    const data = await response.json()
    return { success: true, data }
  } catch (error) {
    clearTimeout(timeoutId)

    // Handle timeout specifically
    if (error instanceof Error && error.name === 'AbortError') {
      apiLogger.error('API call timeout', { endpoint, timeout })
      return {
        success: false,
        error: `Request timeout: The ORC operation took longer than expected (${timeout / 1000}s). Please try again.`,
      }
    }

    apiLogger.error('API call failed', error)
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error occurred',
    }
  }
}

// Server Actions
export async function submitRiskCheck(
  prevState: any,
  formData: FormData
): Promise<{ success: boolean; data?: RiskCheckResult | null; error?: string | null }> {
  try {
    // CSRF Protection: Verify token before processing
    const csrfValidation = await validateCsrfFromFormData(formData)
    if (!csrfValidation.success) {
      apiLogger.error('CSRF validation failed for ORC risk check')
      return {
        success: false,
        data: null,
        error: csrfValidation.error || 'Security validation failed',
      }
    }

    // Rate limiting check
    const session = await getAuthSession()
    if (!session?.userId) {
      return { success: false, data: null, error: 'Authentication required' }
    }

    const userId = session.userId
    if (!RateLimiter.checkLimit(`orc_risk_check_${userId}`, 10, 3600000)) { // 10 requests per hour
      return {
        success: false,
        data: null,
        error: 'Too many risk check requests. Please wait before trying again.'
      }
    }

    // Extract raw data from FormData with proper handling of nested objects
    const rawData = {
      companyName: formData.get('companyName'),
      businessType: formData.get('businessType'),
      industry: formData.get('industry'),
      address: {
        street: formData.get('address.street'),
        city: formData.get('address.city'),
        state: formData.get('address.state'),
        postalCode: formData.get('address.postalCode'),
        country: formData.get('address.country'),
      },
      companySize: formData.get('companySize'),
      annualRevenue: formData.get('annualRevenue'),
      establishedDate: formData.get('establishedDate'),
      website: formData.get('website'),
      primaryContact: {
        name: formData.get('primaryContact.name'),
        title: formData.get('primaryContact.title'),
        email: formData.get('primaryContact.email'),
        phone: formData.get('primaryContact.phone'),
      },
      cyberRisk: formData.get('cyberRisk'),
      sanctionsCheck: formData.get('sanctionsCheck'),
      businessVerification: formData.get('businessVerification'),
      financialVerification: formData.get('financialVerification'),
      reputationCheck: formData.get('reputationCheck'),
      additionalInfo: formData.get('additionalInfo'),
      regulatedIndustry: formData.get('regulatedIndustry'),
      regulatoryLicenses: formData.get('regulatoryLicenses') ?
        JSON.parse(formData.get('regulatoryLicenses') as string) : [],
      operatesInRestrictedRegions: formData.get('operatesInRestrictedRegions'),
      restrictedRegions: formData.get('restrictedRegions') ?
        JSON.parse(formData.get('restrictedRegions') as string) : [],
      beneficialOwners: formData.get('beneficialOwners') ?
        JSON.parse(formData.get('beneficialOwners') as string) : [],
    }

    // Validate and sanitize input using Zod schema
    const riskCheckData = orcRiskCheckSchema.parse(rawData)

    // Log the risk check attempt
    businessLogger.info('Submitting ORC risk check', { companyName: riskCheckData.companyName })

    // Make API call to submit the risk check
    const result = await makeAPICall<RiskCheckResult>('/api/orc/check', {
      method: 'POST',
      body: JSON.stringify({
        ...riskCheckData,
        timestamp: new Date().toISOString(),
      }),
    })

    if (!result.success) {
      return { success: false, data: null, error: result.error || null }
    }

    // Revalidate relevant caches
    revalidateTag('orc-data')
    revalidateTag('orc-checks')

    return { success: true, data: result.data || null, error: null }
  } catch (error) {
    apiLogger.error('ORC risk check failed', error)

    // Handle validation errors specifically
    if (error instanceof ZodError) {
      const errorMessages = error.issues.map(err => `${err.path.join('.')}: ${err.message}`).join(', ')
      return {
        success: false,
        data: null,
        error: `Validation error: ${errorMessages}`,
      }
    }

    return {
      success: false,
      data: null,
      error: 'Failed to submit risk check. Please try again.',
    }
  }
}

