    for (let i = 0; i < companies.length; i++) {
      try {
        const validatedCompany = orcRiskCheckSchema.parse(companies[i])
        validatedCompanies.push(validatedCompany)
      } catch (error) {
        if (error instanceof ZodError) {
          const errorMessages = error.issues.map(err => `${err.path.join('.')}: ${err.message}`).join(', ')
          return {
            success: false,
            data: null,
            error: `Validation error for company ${i + 1}: ${errorMessages}`,
          }
        }
        return {
          success: false,
          data: null,
          error: `Invalid data for company ${i + 1}`,
        }
      }
    }

    businessLogger.info('Submitting bulk ORC risk checks', {
      companyCount: validatedCompanies.length
    })

    // Make API call to submit bulk checks
    const result = await makeAPICall<{ batchId: string; count: number }>('/api/orc/bulk', {
      method: 'POST',
      body: JSON.stringify({
        companies: validatedCompanies,
        timestamp: new Date().toISOString(),
      }),
    })

    if (!result.success) {
      return { success: false, data: null, error: result.error || null }
    }

    // Revalidate relevant caches
    revalidateTag('orc-checks')
    revalidateTag('orc-data')

    return { success: true, data: result.data || null, error: null }
  } catch (error) {
    apiLogger.error('ORC bulk processing failed', error)

    return {
      success: false,
      data: null,
      error: 'Failed to submit bulk risk checks. Please try again.',
    }
  }
}

// Utility action for refreshing ORC data
export async function refreshORCData() {
  revalidateTag('orc-data')
  revalidateTag('orc-checks')
  revalidateTag('orc-drafts')
  revalidateTag('orc-exports')
  revalidateTag('orc-bulk')
}

