'use server'

import { revalidateTag } from 'next/cache'
import { auth } from '@clerk/nextjs/server'
import {
  orcRiskCheckSchema,
  orcDraftSchema,
  orcPollingSchema,
  orcExportSchema,
  type ORCRiskCheckData,
  type ORCDraftData,
  type ORCPollingData,
  type ORCExportData,
  RateLimiter
} from '@/lib/schemas'
import { apiLogger, businessLogger, validationLogger } from '@/lib/logger'
import { validateCsrfFromFormData } from '@/app/_lib/csrf'
import { ZodError } from 'zod'

// Types from validators (keeping for backwards compatibility)

interface RiskCheckResult {
  irn: string
  status: 'pending' | 'completed' | 'failed'
  riskScore: number
  riskLevel: 'low' | 'medium' | 'high'
  checks: {
    cyber: { status: string; score: number }
    sanctions: { status: string; matches: number }
    business: { status: string; verified: boolean }
  }
  createdAt: string
}

// Helper function to get auth session
async function getAuthSession() {
  return await auth()
}

// Timeout configuration based on operation type
const TIMEOUT_CONFIG = {
  RISK_CHECK: 30000,   // 30s - Risk check operations
  DRAFT: 10000,        // 10s - Quick draft saves
  POLLING: 10000,      // 10s - Status polling
  EXPORT: 60000,       // 60s - Report generation/export
  BULK: 60000,         // 60s - Bulk processing
}

// Helper function to determine timeout based on endpoint
function getTimeoutForEndpoint(endpoint: string): number {
  if (endpoint.includes('/check')) return TIMEOUT_CONFIG.RISK_CHECK
  if (endpoint.includes('/draft')) return TIMEOUT_CONFIG.DRAFT
  if (endpoint.includes('/status')) return TIMEOUT_CONFIG.POLLING
  if (endpoint.includes('/export')) return TIMEOUT_CONFIG.EXPORT
  if (endpoint.includes('/bulk')) return TIMEOUT_CONFIG.BULK
  return 30000 // Default 30s
}

// Helper function to make API calls with secure JWT tokens
async function makeAPICall<T>(
  endpoint: string,
  options: RequestInit = {}
): Promise<{ success: boolean; data?: T; error?: string }> {
  const timeout = getTimeoutForEndpoint(endpoint)
  const controller = new AbortController()
  const timeoutId = setTimeout(() => controller.abort(), timeout)

  try {
    const { userId, getToken } = await getAuthSession()
    if (!userId) {
      clearTimeout(timeoutId)
      return { success: false, error: 'Authentication required' }
    }

    // Get the secure JWT access token from Clerk
    const accessToken = await getToken()
    if (!accessToken) {
      apiLogger.error('No access token from Clerk')
      clearTimeout(timeoutId)
      return { success: false, error: 'Invalid authentication token' }
    }

    // Get API base URL from environment variables with proper fallback
    const getApiBaseUrl = (): string => {
      if (process.env['API_BASE_URL']) {
        return process.env['API_BASE_URL']
      }

      // Development fallback - only in development environment
      if (process.env.NODE_ENV === 'development') {
        return 'http://localhost:3001'
      }

      // Production should always have proper environment variables set
      throw new Error('API_BASE_URL must be configured in production')
    }

    const baseURL = getApiBaseUrl()
    const response = await fetch(`${baseURL}${endpoint}`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${accessToken}`, // Use secure JWT token
        ...options.headers,
      },
      cache: 'no-store',
      signal: controller.signal,
      ...options,
    })

    clearTimeout(timeoutId)

    if (!response.ok) {
      const errorData = await response.text()
      return {
        success: false,
        error: `API Error: ${response.status} - ${errorData}`,
      }
    }

    const data = await response.json()
    return { success: true, data }
  } catch (error) {
    clearTimeout(timeoutId)

    // Handle timeout specifically
    if (error instanceof Error && error.name === 'AbortError') {
      apiLogger.error('API call timeout', { endpoint, timeout })
      return {
        success: false,
        error: `Request timeout: The ORC operation took longer than expected (${timeout / 1000}s). Please try again.`,
      }
    }

    apiLogger.error('API call failed', error)
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error occurred',
    }
  }
}

