      companyName: formData.get('companyName'),
      businessType: formData.get('businessType'),
      industry: formData.get('industry'),
      address: {
        street: formData.get('address.street'),
        city: formData.get('address.city'),
        state: formData.get('address.state'),
        postalCode: formData.get('address.postalCode'),
        country: formData.get('address.country'),
      },
      companySize: formData.get('companySize'),
      annualRevenue: formData.get('annualRevenue'),
      establishedDate: formData.get('establishedDate'),
      website: formData.get('website'),
      primaryContact: {
        name: formData.get('primaryContact.name'),
        title: formData.get('primaryContact.title'),
        email: formData.get('primaryContact.email'),
        phone: formData.get('primaryContact.phone'),
      },
      cyberRisk: formData.get('cyberRisk'),
      sanctionsCheck: formData.get('sanctionsCheck'),
      businessVerification: formData.get('businessVerification'),
      additionalInfo: formData.get('additionalInfo'),
      draftName: formData.get('draftName'),
      lastModified: new Date().toISOString(),
    }

    // Validate and sanitize input using relaxed draft schema
    const draftData = orcDraftSchema.parse(rawData)

    businessLogger.info('Saving ORC draft', { companyName: draftData.companyName || 'Unnamed draft' })

    // Make API call to save draft
    const result = await makeAPICall<{ draftId: string }>('/api/orc/draft', {
      method: 'POST',
      body: JSON.stringify({
        ...draftData,
        timestamp: new Date().toISOString(),
      }),
    })

    if (!result.success) {
      return { success: false, data: null, error: result.error || null }
    }

    return { success: true, data: result.data || null, error: null }
  } catch (error) {
    apiLogger.error('Save ORC draft failed', error)

    // Handle validation errors specifically
    if (error instanceof ZodError) {
      const errorMessages = error.issues.map(err => `${err.path.join('.')}: ${err.message}`).join(', ')
      return {
        success: false,
        data: null,
        error: `Validation error: ${errorMessages}`,
      }
    }

    return {
      success: false,
      data: null,
      error: 'Failed to save draft. Please try again.',
    }
  }
}

// ORC polling action for status updates
export async function pollRiskCheckStatus(
  irn: string,
  pollInterval?: number
): Promise<{ success: boolean; data?: RiskCheckResult | null; error?: string | null }> {
  try {
    // Rate limiting check
    const session = await getAuthSession()
    if (!session?.userId) {
      return { success: false, data: null, error: 'Authentication required' }
    }

    const userId = session.userId
    if (!RateLimiter.checkLimit(`orc_poll_${userId}`, 100, 3600000)) { // 100 requests per hour
      return {
        success: false,
        data: null,
        error: 'Too many polling requests. Please wait before trying again.'
      }
    }

    // Validate and sanitize input
    const pollingData = orcPollingSchema.parse({ irn, pollInterval })

    businessLogger.info('Polling ORC status', { irn: pollingData.irn })

    // Make API call to get status
    const result = await makeAPICall<RiskCheckResult>(`/api/orc/status/${pollingData.irn}`, {
      method: 'GET',
      cache: 'no-store',
    })

    if (!result.success) {
      return { success: false, data: null, error: result.error || null }
    }

    return { success: true, data: result.data || null, error: null }
  } catch (error) {
    apiLogger.error('ORC polling failed', error)

    // Handle validation errors specifically
    if (error instanceof ZodError) {
      const errorMessages = error.issues.map(err => `${err.path.join('.')}: ${err.message}`).join(', ')
      return {
        success: false,
        data: null,
        error: `Validation error: ${errorMessages}`,
      }
    }

    return {
      success: false,
      data: null,
      error: 'Failed to poll risk check status. Please try again.',
    }
  }
}

// ORC export action
