import { z } from 'zod'
import { secureText, secureEmail, securePhone, securePostalCode, secureBoolean } from './security'

/**
 * ORC (Onboard Risk Check) validation schemas with comprehensive security measures
 */

// Industry classification for risk assessment
const industrySchema = z.enum([
  'technology',
  'financial_services',
  'healthcare',
  'retail',
  'manufacturing',
  'real_estate',
  'energy',
  'telecommunications',
  'transportation',
  'education',
  'government',
  'non_profit',
  'agriculture',
  'mining',
  'construction',
  'hospitality',
  'entertainment',
  'legal_services',
  'consulting',
  'other'
], { errorMap: () => ({ message: 'Please select a valid industry' }) })

// Business type for ORC assessment
const businessTypeSchema = z.enum([
  'corporation',
  'llc',
  'partnership',
  'sole_proprietorship',
  'non_profit',
  'government_entity',
  'financial_institution',
  'money_service_business',
  'virtual_currency_business',
  'trust',
  'foundation',
  'other'
], { errorMap: () => ({ message: 'Please select a valid business type' }) })

// Country validation with risk assessment considerations
const countrySchema = z.string()
  .length(2, 'Country code must be exactly 2 characters')
  .regex(/^[A-Z]{2}$/, 'Country code must be uppercase letters only')

// Address validation for comprehensive risk assessment
const addressSchema = z.object({
  street: secureText(200).refine(val => val.length >= 5, 'Street address must be at least 5 characters'),
  city: secureText(50).refine(val => val.length >= 2, 'City must be at least 2 characters'),
  state: secureText(50).refine(val => val.length >= 2, 'State/Province must be at least 2 characters'),
  postalCode: securePostalCode,
  country: countrySchema,
})

// Company size validation for risk assessment
const companySizeSchema = z.enum([
  'micro',      // 1-10 employees
  'small',      // 11-50 employees
  'medium',     // 51-250 employees
  'large',      // 251-1000 employees
  'enterprise'  // 1000+ employees
], { errorMap: () => ({ message: 'Please select a valid company size' }) })

// Annual revenue validation for risk assessment
const annualRevenueSchema = z.enum([
  'under_100k',
  '100k_500k',
  '500k_1m',
  '1m_5m',
  '5m_10m',
  '10m_50m',
  '50m_100m',
  'over_100m'
], { errorMap: () => ({ message: 'Please select a valid annual revenue range' }) })

// Main ORC risk check validation schema
export const orcRiskCheckSchema = z.object({
  // Basic company information
  companyName: secureText(200)
    .refine(val => val.length >= 2, 'Company name must be at least 2 characters')
    .refine(
      (name) => /[a-zA-Z]/.test(name),
      { message: 'Company name must contain letters' }
    ),

  businessType: businessTypeSchema,
  industry: industrySchema,

  // Company address
  address: addressSchema,

  // Company details
  companySize: companySizeSchema,
  annualRevenue: annualRevenueSchema,

  establishedDate: z.string()
    .regex(/^\d{4}-\d{2}-\d{2}$/, 'Date must be in YYYY-MM-DD format')
    .refine((date) => {
      const parsed = new Date(date)
      const now = new Date()
      const hundredYearsAgo = new Date(now.getFullYear() - 100, now.getMonth(), now.getDate())
      return parsed >= hundredYearsAgo && parsed <= now
    }, 'Established date must be within the last 100 years and not in the future'),

  // Website validation with security checks
  website: z.string()
    .url('Website must be a valid URL')
    .max(200, 'Website URL must be 200 characters or less')
    .optional()
    .transform((val) => val ? val.toLowerCase() : undefined)
    .refine(
      (url) => {
        if (!url) return true
        // Check for suspicious domains or patterns
        const suspiciousPatterns = [
          /[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}/, // IP addresses
          /localhost/,
          /\.onion$/,
          /[^a-z0-9\-\.]/
        ]
        return !suspiciousPatterns.some(pattern => pattern.test(url))
      },
      { message: 'Website URL appears to be suspicious or invalid' }
    ),

  // Primary contact information
  primaryContact: z.object({
    name: secureText(100).refine(val => val.length >= 2, 'Contact name must be at least 2 characters'),
    title: secureText(100).refine(val => val.length >= 2, 'Contact title must be at least 2 characters'),
    email: secureEmail,
    phone: securePhone,
  }),

  // Risk assessment options
  cyberRisk: secureBoolean,
  sanctionsCheck: secureBoolean,
  businessVerification: secureBoolean,
  financialVerification: secureBoolean.optional().default(false),
  reputationCheck: secureBoolean.optional().default(false),

  // Additional information
  additionalInfo: secureText(2000).optional(),

  // Regulatory considerations
  regulatedIndustry: secureBoolean.optional().default(false),
  regulatoryLicenses: z.array(z.object({
    type: secureText(100),
    number: secureText(100),
    issuingAuthority: secureText(100),
    expirationDate: z.string()
      .regex(/^\d{4}-\d{2}-\d{2}$/, 'Date must be in YYYY-MM-DD format')
      .optional(),
  }))
    .max(10, 'Maximum 10 regulatory licenses allowed')
    .optional()
    .default([]),

  // Geographic restrictions check
  operatesInRestrictedRegions: secureBoolean.optional().default(false),
  restrictedRegions: z.array(countrySchema)
    .max(50, 'Maximum 50 restricted regions allowed')
    .optional()
    .default([]),

  // Beneficial ownership information (if required)
  beneficialOwners: z.array(z.object({
    name: secureText(100).refine(val => val.length >= 2, 'Beneficial owner name is required'),
    ownershipPercentage: z.number()
      .min(0, 'Ownership percentage must be at least 0')
      .max(100, 'Ownership percentage cannot exceed 100'),
    isPoliticallyExposed: secureBoolean.optional().default(false),
    nationality: countrySchema.optional(),
  }))
    .max(10, 'Maximum 10 beneficial owners allowed')
    .optional()
    .default([]),
}).refine(
  (data) => {
    // Validate business metrics consistency
    const establishedYear = new Date(data.establishedDate).getFullYear()
    const currentYear = new Date().getFullYear()
    const yearsInBusiness = currentYear - establishedYear

    // Check consistency between company size, revenue, and years in business
    if (yearsInBusiness < 2) {
      if (data.companySize === 'enterprise' ||
          ['50m_100m', 'over_100m'].includes(data.annualRevenue)) {
        return false
      }
    }

    return true
  },
  {
    message: 'Company metrics appear inconsistent with business age',
    path: ['annualRevenue']
  }
).refine(
  (data) => {
    // If operating in restricted regions, additional checks required
    if (data.operatesInRestrictedRegions && data.restrictedRegions.length === 0) {
      return false
    }
    return true
  },
  {
    message: 'Restricted regions must be specified if operating in restricted areas',
    path: ['restrictedRegions']
  }
).refine(
  (data) => {
    // If regulated industry, licenses may be required
    if (data.regulatedIndustry && data.regulatoryLicenses.length === 0) {
      // Warning but not error - some regulated industries may not require licenses
      return true
    }
    return true
  },
  {
    message: 'Regulatory licenses may be required for regulated industries',
    path: ['regulatoryLicenses']
  }
)

// ORC draft save validation schema (more relaxed)
export const orcDraftSchema = z.object({
  companyName: secureText(200).refine(val => val.length >= 1, 'Company name is required for draft'),
  businessType: businessTypeSchema.optional(),
  industry: industrySchema.optional(),

  // Partial address (only required fields for draft)
  address: z.object({
    street: secureText(200).optional(),
    city: secureText(50).optional(),
    state: secureText(50).optional(),
    postalCode: secureText(20).optional(),
    country: countrySchema.optional(),
  }).optional(),

  // Optional fields for draft
  companySize: companySizeSchema.optional(),
  annualRevenue: annualRevenueSchema.optional(),
  establishedDate: z.string()
    .regex(/^\d{4}-\d{2}-\d{2}$/, 'Date must be in YYYY-MM-DD format')
    .optional(),
  website: z.string().url().optional(),

  primaryContact: z.object({
    name: secureText(100).optional(),
    title: secureText(100).optional(),
    email: z.string().email().optional(),
    phone: secureText(20).optional(),
  }).optional(),

  cyberRisk: secureBoolean.optional(),
  sanctionsCheck: secureBoolean.optional(),
  businessVerification: secureBoolean.optional(),
  additionalInfo: secureText(2000).optional(),

  // Draft metadata
  draftName: secureText(100).optional(),
  lastModified: z.string().datetime().optional(),
})

// ORC results polling validation schema
export const orcPollingSchema = z.object({
  irn: secureText(50)
    .refine(val => val.length >= 1, 'IRN is required')
    .refine(val => /^[A-Z0-9\-_]{1,50}$/.test(val), 'IRN format is invalid'),

  pollInterval: z.number()
    .min(5, 'Minimum poll interval is 5 seconds')
    .max(300, 'Maximum poll interval is 5 minutes')
    .optional()
    .default(30),
})

// ORC export validation schema
export const orcExportSchema = z.object({
  irn: secureText(50)
    .refine(val => val.length >= 1, 'IRN is required')
    .refine(val => /^[A-Z0-9\-_]{1,50}$/.test(val), 'IRN format is invalid'),

  format: z.enum(['pdf', 'csv', 'json', 'xml'], {
    errorMap: () => ({ message: 'Please select a valid export format' })
  }),

  includeCharts: secureBoolean.optional().default(false),
  includeRawData: secureBoolean.optional().default(false),
  includeSummary: secureBoolean.optional().default(true),

  sections: z.array(z.enum([
    'executive_summary',
    'company_overview',
    'risk_scores',
    'cyber_assessment',
    'sanctions_results',
    'business_verification',
    'recommendations',
    'appendices'
  ]))
    .min(1, 'At least one section must be selected')
    .optional()
    .default(['executive_summary', 'risk_scores']),
})

// Type exports for TypeScript
export type ORCRiskCheckData = z.infer<typeof orcRiskCheckSchema>
export type ORCDraftData = z.infer<typeof orcDraftSchema>
export type ORCPollingData = z.infer<typeof orcPollingSchema>
export type ORCExportData = z.infer<typeof orcExportSchema>
