import type { NextConfig } from "next";

const nextConfig: NextConfig = {
  // Turbopack configuration (moved from experimental.turbo)
  turbopack: {
    // Additional Turbopack configuration can go here
  },
  // Server component configuration
  serverActions: {
    bodySizeLimit: '1mb',
  },
  // Image optimization configuration
  images: {
    formats: ['image/avif', 'image/webp'],
    deviceSizes: [640, 750, 828, 1080, 1200, 1920],
    imageSizes: [16, 32, 48, 64, 96, 128, 256, 384],
    // Allow optimization of SVG files from public directory
    dangerouslyAllowSVG: true,
    contentSecurityPolicy: "default-src 'self'; script-src 'none'; sandbox;",
  },
  eslint: {
    // Warning: This allows production builds to successfully complete even if
    // your project has ESLint errors.
    ignoreDuringBuilds: true,
  },
  typescript: {
    // Warning: This allows production builds to successfully complete even if
    // your project has TypeScript errors.
    ignoreBuildErrors: false,
  },
};

export default nextConfig;

import { dirname } from "path";
import { fileURLToPath } from "url";
import { FlatCompat } from "@eslint/eslintrc";
import security from "eslint-plugin-security";
import noSecrets from "eslint-plugin-no-secrets";
import antiTrojanSource from "eslint-plugin-anti-trojan-source";

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const compat = new FlatCompat({
  baseDirectory: __dirname,
});

const eslintConfig = [
  ...compat.extends("next/core-web-vitals", "next/typescript"),
  {
    ignores: [
      "node_modules/**",
      ".next/**",
      "out/**",
      "build/**",
      "next-env.d.ts",
      "api-server/dist/**",
    ],
  },
  {
    files: ["**/*.{js,jsx,ts,tsx}"],
    plugins: {
      security,
      "no-secrets": noSecrets,
      "anti-trojan-source": antiTrojanSource,
      "@microsoft/sdl": await import("@microsoft/eslint-plugin-sdl").then(m => m.default),
    },
    rules: {
      // Security rules
      "security/detect-buffer-noassert": "error",
      "security/detect-child-process": "error",
      "security/detect-disable-mustache-escape": "error",
      "security/detect-eval-with-expression": "error",
      "security/detect-no-csrf-before-method-override": "error",
      "security/detect-non-literal-fs-filename": "error",
      "security/detect-non-literal-regexp": "error",
      "security/detect-non-literal-require": "error",
      "security/detect-object-injection": "error",
      "security/detect-possible-timing-attacks": "error",
      "security/detect-pseudoRandomBytes": "error",
      "security/detect-unsafe-regex": "error",

      // No secrets rules
      "no-secrets/no-secrets": "error",

      // Anti-trojan source rules
      "anti-trojan-source/no-bidi": "error",

      // Microsoft SDL rules
      "@microsoft/sdl/no-html-method": "error",
      "@microsoft/sdl/no-inner-html": "error",
      "@microsoft/sdl/no-document-write": "error",
      "@microsoft/sdl/no-document-domain": "error",
      "@microsoft/sdl/no-insecure-url": "error",
      "@microsoft/sdl/no-cookies": "warn",
      "@microsoft/sdl/no-postmessage-star-origin": "error",
      "@microsoft/sdl/no-insecure-random": "error",
    },
  },
];

export default eslintConfig;

